@import url('https://fonts.googleapis.com/css2?family=Roboto:wght@400;700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Tenor+Sans&display=swap');

body {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
  padding: 0;
  margin: 0 auto;
  font-family: 'Roboto';
}
.shared-width {
  max-width: 1224px;
  margin: 0 auto;
}
#login--header-main {
  width: 100%;
  background: #ffffff;
  display: flex;
  justify-content: center;
  align-items: center;
}
.login--header-wrapper {
  width: 100%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  height: 96px;
  padding: 10px 0;
}
.hyatt-brand-logo {
  width: 134px;
  aspect-ratio: 2.36;
}
/* Button start */
.login--custom-button--medium {
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  gap: 8px;
  width: 164px;
  height: 40px;
  border-radius: 100px;
}
.custom-button--secondary {
  border: 2px solid #26828e;
  color: #26828e;
  background-color: #ffffff;
  cursor: pointer;
}
.custom-button--secondary:hover {
  background-color: #d8f1f5;
}
.login--custom-button--small {
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 0px 32px;
  gap: 12px;
  width: 105px;
  height: 48px;
  border-radius: 100px;
}
.custom-button--primary {
  border: 2px solid #ffffff;
  color: #ffffff;
  background-color: #26828e;
  cursor: pointer;
}
.custom-button--primary:hover {
  background-color: #31a7b6;
}
.custom-button--primary[disabled] {
  opacity: 0.5;
  cursor: not-allowed;
}
/* Button end */
.login--dropdown-list-wrapper {
  position: relative;
  width: 100%;
  display: flex;
  flex-direction: column;
}
.login--custom-button-label {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 700;
  font-size: 14px;
  line-height: 125%;
  text-align: center;
  text-transform: capitalize;
}

.login--button-section {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  padding: 0px;
  width: 100%;
}
.login--button-section-overline {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  font-size: 14px;
  line-height: 175%;
  color: #696d6f;
}
.login--button-section-overline a {
  display: inline;
}
.login--page-main-section {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 0px;
  width: 100%;
  border-radius: 0px 0px 96px 0px;
  background: #26828e;
}
.login--hero-image-wrapper {
  background-color: #26828e;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 0px;
  isolation: isolate;
  width: 100%;
  height: 800px;
}
.login--hero-image {
  width: 100%;
  height: 800px;
  background: linear-gradient(0deg, rgba(0, 0, 0, 0.2), rgba(0, 0, 0, 0.2)),
    url('https://dev3-content.ii-apps.com/files/live/sites/hrc-marketing/files/davinci-images/bg_login.png');
  border-radius: 0px 0px 96px 0px;
  background-repeat: no-repeat;
  background-size: cover;
  z-index: 0;
  display: flex;
  align-items: center;
  justify-content: center;
}
.login--hero-content {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 40px;
  gap: 40px;
  background: #ffffff;
  border-radius: 16px;
  z-index: 1;
  max-width: 600px;
}
.login--hero-content-heading-section {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 0px;
  gap: 24px;
}
.login--hero-content-heading {
  font-family: 'Tenor Sans';
  font-style: normal;
  font-weight: 400;
  font-size: 40px;
  line-height: 100%;
  /* identical to box height, or 40px */

  /* Neutral/HVC Dark Grey */

  color: #222222;
}
.login--hero-content-heading-overline {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 175%;
  color: #696d6f;
}
.login--hero-content-heading-overline ul {
  padding: 0 0 0 20px;
}
.login--form-textfield-wrapper {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 0px;
  gap: 24px;
  width: 100%;
}
.login--form-textfield-wrapper.login-page {
  gap: 17px;
}
.login--custom-textfield {
  position: relative;
  width: 100%;
  display: flex;
}
.login--custom-textfield span {
  position: absolute;
  top: 50%;
  right: 15px;
  display: flex;
  transform: translateY(-50%);
  cursor: pointer;
}
.login--custom-textfield span img {
  width: 24px;
  height: 24px;
}
.login--custom-Label {
  position: absolute;
  font-size: 1rem;
  left: 0;
  top: 50%;
  transform: translateY(-50%);
  background-color: #ffffff;
  color: #696d6f;
  padding: 0 0.3rem;
  margin: 0 0.5rem;
  transition: 0.1s ease-out;
  transform-origin: left top;
  pointer-events: none;
}
.login--custom-input {
  flex: 1;
  font-size: 1rem;
  outline: none;
  border: 1px solid #696d6f;
  border-radius: 5px;
  padding: 1rem 0.7rem;
  color: #696d6f;
  transition: 0.1s ease-out;
}
.login--custom-input-error {
  border: 1px solid #c54c34 !important;
}
.login--custom-checkbox-error {
  outline: 2px solid #c54c34;
  outline-offset: -2px;
}
.login--custom-input:focus {
  border-color: #696d6f;
}
.login--custom-input:focus + .login--custom-Label {
  color: #696d6f;
  top: 0;
  transform: translateY(-50%) scale(0.9);
}
.login--custom-input:not(:placeholder-shown) + .login--custom-Label {
  top: 0;
  transform: translateY(-50%) scale(0.9);
}
.login--custom-input:focus + .login--custom-Label-error {
  color: #c54c34;
}
.login--custom-Label-error {
  color: #c54c34;
}
.login--error-description {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  font-size: 12px;
  line-height: 175%;
  text-align: left;
  color: #c54c34;
  margin-top: -10px;
}
.icon {
  position: absolute;
  right: 2%;
  top: 35%;
}
a {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 700;
  font-size: 14px;
  line-height: 175%;
  /* identical to box height, or 24px */
  text-decoration: none;
  text-align: right;
  float: right;
  /* HVC Blue */

  color: #26828e;
}
.login--entry-forget {
  display: flex;
  width: 100%;
  justify-content: right;
}
.login--checkbox {
  display: flex;
  flex-direction: row;
  align-items: center;
  padding: 0px;
  gap: 8px;
}
.login--checkbox-input {
  width: 16px;
  height: 16px;
  background: #696d6f;
}
.login--checkbox-label {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 175%;
  color: #696d6f;
}
.login--checkbox-label a {
  font-size: 16px;
  line-height: 175%;
}
.login--dropdown-list {
  z-index: 2;
  width: -webkit-fill-available;
  font-size: 1rem;
  outline: none;
  border: 1px solid #696d6f;
  border-radius: 0px 0px 5px 5px;
  padding: 1rem 0.7rem;
  color: #696d6f;
  transition: 0.1s ease-out;
  position: absolute;
  top: 100%;
  background: white;
}
/*footer start */
footer {
  background: #26828e;
  width: 100%;
}
.login--main-footer {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 80px 0px 40px;
  width: 100%;
  gap: 24px;
}
.login--footer-main-section {
  display: flex;
  flex-direction: row;
  align-items: flex-start;
  padding: 0px;
  width: 100%;
  gap: 32px;
}
.login--footer-logo {
  width: 23.5%;
}
.login--footer-detail {
  width: 37.5%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 0px;
  gap: 32px;
}
.login--footer-have-questions {
  width: 20%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 0px;
  gap: 32px;
}
.login--footer-social-media {
  width: 19%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 0px;
  gap: 8px;
}
.login--footer-question-details {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
  padding: 0px;
  gap: 4px;
}
.social-media-icons {
  display: flex;
  flex-direction: row;
  align-items: flex-start;
  padding: 0px;
  gap: 8px;
}
.login--foter-website-wrapper {
  margin-left: 24.4%;
}
.login--footer-website-section {
  align-items: flex-start;
  display: flex;
  flex-direction: row;
  gap: 16px;
}
.login--footer-website-section a {
  font-weight: 400;
  font-size: 12px;
}
.login--footer-question-details a {
  font-size: 16px;
  line-height: 175%;
  font-weight: 700;
}
.login--footer-question-details p {
  font-size: 16px;
  line-height: 150%;
}
.login--footer-contact-from a {
  font-size: 16px;
  line-height: 150%;
  font-weight: 700;
}
footer p,
footer h5 {
  margin: 0;
  color: #ffffff;
  font-family: 'Roboto';
}
footer p {
  line-height: 175%;
  font-weight: 400;
  font-size: 12px;
}
footer h5 {
  line-height: 150%;
  font-weight: 700;
  text-transform: uppercase;
}
footer a {
  color: #ffffff;
  font-family: 'Roboto';
  text-decoration: underline;
}
/*footer end*/
/*checkbox modification -css*/
input[type='checkbox'] {
  accent-color: #26828e;
}
/*media qury css ans classes - start*/
@media screen and (max-width: 1223.99px) {
  .shared-width {
    max-width: none;
    width: 96%;
    margin: 0 auto;
  }
}
@media screen and (max-width: 1199.99px) and (min-width: 600px) {
  .login--button-section {
    flex-direction: column;
    align-items: flex-start;
    gap: 24px;
  }
  .login--hero-content {
    padding: 40px 24px;
    margin: auto;
  }
  .login--footer-main-section {
    flex-direction: column;
  }
  .login--footer-logo,
  .login--footer-detail,
  .login--footer-have-questions,
  .login--footer-social-media,
  .login--foter-website-wrapper {
    width: 81%;
  }
  .login--foter-website-wrapper {
    margin: 0;
  }
  .login--hero-image {
    border-radius: 0px;
  }
}
@media screen and (max-width: 850px) {
}
@media screen and (max-width: 599.99px) {
  .login--button-section {
    flex-direction: column;
    align-items: flex-start;
    gap: 24px;
  }
  .login--hero-content {
    padding: 40px 24px;
  }
  .login--footer-main-section {
    flex-direction: column;
  }
  .login--footer-logo,
  .login--footer-detail,
  .login--footer-have-questions,
  .login--footer-social-media,
  .login--foter-website-wrapper {
    width: 81%;
  }
  .login--foter-website-wrapper {
    margin: 0;
  }
  .login--footer-website-section {
    display: inline-grid;
    width: 100%;
    grid-template-columns: auto auto;
  }
  .login--footer-website-section div {
    display: flex;
    justify-content: left;
  }
  .login--hero-image {
    border-radius: 0px;
  }
  .telephone-paragraph span {
    display: inline-block !important;
    vertical-align: middle;
  }
}
/*media qury css ans classes - end*/

/* Create Password */
.list-wrapper {
  display: flex;
  align-items: center;
  gap: 10px;
}
span.valid {
  background-image: url('https://dev3-content.ii-apps.com/files/live/sites/hrc-marketing/files/davinci-images/valid-icon.png');
  width: 24px;
  height: 24px;
  display: inline-flex;
}

span.invalid {
  background-image: url('https://dev3-content.ii-apps.com/files/live/sites/hrc-marketing/files/davinci-images/invalid-icon.png');
  width: 24px;
  height: 24px;
  display: inline-flex;
}
/* Create Password */
.telephone-paragraph span {
  display: inline-flex;
  align-items: center;
}

.custom-select-wrapper {
  display: flex;
  width: 100%;
  flex-direction: column;
  position: relative;
}

.custom-select-wrapper-inner {
  display: flex;
  justify-content: space-between;
  padding: 12px 16px;
  border: 1px solid #696d6f;
  border-radius: 8px;
  cursor: pointer;
  position: relative;
}
#custom-select-label {
  position: absolute;
  top: -1px;
  transform: translateY(-50%);
  background: #fff;
  font-size: 14px;
  color: #696d6f;
  visibility: hidden;
  opacity: 0;
  transition: visibility 0s, opacity 0.1s ease-in-out;
}

.custom-selectoptions {
  list-style: none;
  padding: 0;
  margin: 0;
}

.custom-selectoptions {
  position: absolute;
  width: 100%;
  top: 45px;
  list-style: none;
  padding: 0;
  margin: 0;
  background: #fff;
  box-shadow: rgba(0, 0, 0, 0.2) 0px 5px 5px -3px, rgba(0, 0, 0, 0.14) 0px 8px 10px 1px,
    rgba(0, 0, 0, 0.12) 0px 3px 14px 2px;
  border-radius: 0 0 8px 8px;
  overflow: hidden;
  z-index: 1;
}

.custom-selectoptions li {
  padding: 12px 16px;
  border-bottom: 1px solid #eee;
  cursor: pointer;
}

.custom-selectoptions li:hover {
  background: #eee;
}

.custom-selectoptions li:last-child {
  border-bottom: none;
}

