module.exports = a = async ({params}) => {
    console.log('params: ', params)
    try {
        const { token, pwRecoveryAttribute, TTL } = params

        if (!token || !pwRecoveryAttribute || typeof TTL !== 'number') {
            throw new Error('Missing required parameter: token, pwRecoveryAttribute, or TTL')
        }

        const [expectedCode, timestampStr] = pwRecoveryAttribute.split('|')

        if (!expectedCode || !timestampStr) {
            throw new Error('Invalid pwRecoveryAttribute format. Expected format: <code>|<timestamp>')
        }

        if (token !== expectedCode) {
            throw new Error('Invalid recovery code')
        }

        const timestamp = parseInt(timestampStr, 10)
        if (isNaN(timestamp)) {
            throw new Error('Invalid timestamp in pwRecoveryAttribute')
        }

        const currentTime = Date.now()
        const ttlInMillis = TTL * 60 * 60 * 1000

        if ((currentTime - timestamp) > ttlInMillis) {
            throw new Error('Token has expired')
        }

        return { isValid: true }
    } catch (error) {
        console.error('Error validating recovery code:', error)
        throw new Error(error.message)
    }
}

