module.exports = a = async ({params}) => {
    console.log('params: ', params)

    try {
        // Ensure params.input exists
        if (!params.input) {
            throw new Error("Missing input parameter")
        }

        // Parse input if it is a string
        const input = typeof params.input === "string" ? JSON.parse(params.input) : params.input

        // Extract the body from payload or rawResponse
        let body
        if (input.payload && input.payload.body) {
            body = JSON.parse(input.payload.body)
        } else if (input.rawResponse) {
            const raw = JSON.parse(input.rawResponse)
            body = JSON.parse(raw.body)
        } else {
            throw new Error("Unable to locate body in input")
        }

        console.log("Parsed body: ", body)

        const { errorCode, message } = body

        if (errorCode === null && message === "Password reset successfully") {
            return { success: true, message }
        } else {
            throw new Error(errorCode || message || "Unknown error occurred")
        }
    } catch (err) {
        console.error("Error: ", err.message)
        throw err
    }
}

