module.exports = a = async ({params}) => {
    console.log('params: ', params)

    try {
        if (!params.input) {
            return {
                error: {
                    code: 500,
                    message:  "Missing input"
                }
            };
        }

        const parsedInput = typeof params.input === 'string' ? JSON.parse(params.input) : params.input;

        if (!parsedInput.rawResponse) {
            return {
                error: {
                    code: 500,
                    message:  "Missing rawResponse in input"
                }
            };
        }

        const rawResponse = JSON.parse(parsedInput.rawResponse);

        if (!rawResponse.body) {
            return {
                error: {
                    code: 500,
                    message:  "Missing body in rawResponse"
                }
            };
        }

        const body = JSON.parse(rawResponse.body);
        if(body.success) {
            return {
                statusCode: rawResponse.statusCode,
                success: body.success,
                authenticationComplete: body.authenticationComplete,
                message: body.message,
                errorCode: body.errorCode,
                accessToken: body.accessToken,
                idToken: body.idToken,
                refreshToken: body.refreshToken,
                tokenType: body.tokenType,
                expiresIn: body.expiresIn,
                challengeName: body.challengeName,
                challengeParameters: body.challengeParameters,
                session: body.session
            }
        } else {
             return {
                error: {
                    code: rawResponse.statusCode || 500,
                    message: body.message || "Unknown error occurred"
                }
            };
        }


    } catch (err) {
        console.error('Error parsing input: ', err)
         return {
                error: {
                    code: 500,
                    message:  "Unknown error occurred"
                }
            };
    }
}
