/* Responsive css======================================================== */
/* ================= Mobile Responsive ================= */
@media (max-width: 767px) {
  .mobile-menu{
    display: block;
    position: fixed;
    top: 0;
    left: -300px;
    width: 300px;
    height: 85%;
    background-color: white;
    box-shadow: 2px 0 5px rgba(0, 0, 0, 0.3);
    transition: left 0.3s ease-in-out;
    z-index: 1000;
    padding: 20px;
  }

  .mobile-menu.open {
    left: 0;
  }

  .side-menu-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-weight: bold;
    font-size: 18px;
    padding-bottom: 10px;
  }

  .close-btn {
    background: none;
    border: none;
    font-size: 24px;
    cursor: pointer;
    color: black;
    margin-left: 59px;
    font-weight: 300;
  }

  .menu-content {
    margin-top: 20px;
  }

  #hamburgerBtn {
    display: block;
    cursor: pointer;
  }

}


