  color: #000;
  line-height: 24px;
}

/* Accordion content */
.accordion-content {
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.4s ease;
  padding: 0;
}

.accordion-content.open {
  /* max-height: 500px;  */
