}
.adjustProperty .open{
  max-height: 1000px;
  max-height: auto;
  padding: 12px 0 8px 24px; /* spacing inside when open */
  width: 712px;
}

/* Rotate arrow when open */
.accordion-header.active .accordion-icon img {
  transform: rotate(180deg);
}

