    if (!input.hasAttribute("readonly")) {
      e.preventDefault();
      // setTimeout(() => {
      input.focus();
      const len = input.value.length;
      input.setSelectionRange(len, len);
      // }, 0);
    }
  }


  // ðŸ‘‰ Handle Edit / Cancel Changes toggle
  toggleLink.addEventListener("click", function (e) {
   e.preventDefault();


    if (input.hasAttribute("readonly")) {
