module.exports = a = async ({params}) => {
  console.log('params: ', params)

  try {
    const { email, phone, mfaDevices } = params

    if (!mfaDevices || !Array.isArray(mfaDevices.devices)) {
      throw new Error('Invalid or missing mfaDevices.devices')
    }

    const normalizeEmail = (value) => {
      if (!value || typeof value !== 'string') return null
      return value.trim().toLowerCase()
    }

    const normalizePhone = (value) => {
      if (!value || typeof value !== 'string') return null
      return value.replace(/[^\d+]/g, '')
    }

    const formatPhone = (raw) => {
      if (!raw) return 'No Phone Number'
      let cleaned = raw.replace(/[^\d+]/g, '')
      if (cleaned.startsWith('1') && cleaned.length === 11) {
        const digits = cleaned.slice(1)
        return `${digits.slice(0,3)}-${digits.slice(3,6)}-${digits.slice(6)}`
      } else if (/^\d{10}$/.test(cleaned)) {
        return `${cleaned.slice(0,3)}-${cleaned.slice(3,6)}-${cleaned.slice(6)}`
      } else if (cleaned.startsWith('+')) {
        return cleaned
      } else {
        return '+' + cleaned
      }
    }

    const normalizedInputEmail = normalizeEmail(email)
    const normalizedInputPhone = normalizePhone(phone)

    let emailStatus = {
      value: normalizedInputEmail || 'No Email Address',
      status: 'not_verified',
      deviceId: ''
    }

    let phoneStatus = {
      value: formatPhone(normalizedInputPhone),
      status: 'not_verified',
      deviceId: ''
    }

     let voiceStatus = {
      value: formatPhone(normalizedInputPhone),
      status: 'not_verified',
      deviceId: ''
    }

    const totpDevices = []


    for (const device of mfaDevices.devices) {
      const { type, status, id } = device
      const isVerified = status === 'ACTIVE'

      if (type === 'EMAIL' && device.email && isVerified) {
        const devEmail = normalizeEmail(device.email)
        if (normalizedInputEmail && devEmail === normalizedInputEmail) {
          emailStatus.status = 'verified'
          emailStatus.deviceId = id || ''
        }
      }

      if (type === 'SMS' && device.phone && isVerified) {
        const devPhoneNormalized = normalizePhone(device.phone)
        if (normalizedInputPhone && devPhoneNormalized === normalizedInputPhone) {
          phoneStatus.status = 'verified'
          phoneStatus.deviceId = id || ''
        }
      }

      if (type === 'VOICE' && device.phone && isVerified) {
        const devPhoneNormalized = normalizePhone(device.phone)
         if (normalizedInputPhone && devPhoneNormalized === normalizedInputPhone) {
          voiceStatus.status = 'verified'
          voiceStatus.deviceId = id || ''
        }
      }


      if (type === 'TOTP' && isVerified) {
        totpDevices.push({
          deviceId: id || '',
          nickname: device.nickname && device.nickname.trim() !== '' 
                    ? device.nickname
                    : 'Authenticator App',
          status: 'verified'
        })
      }
    }

    if (totpDevices.length === 0) {
      totpDevices.push({
        nickname: 'No Authenticator App',
        status: 'not_verified',
        deviceId: ''
      })
    }



    return {
      email: emailStatus,
      phone: phoneStatus,
      totpDevices,
      voiceDevices : voiceStatus
    }

  } catch (err) {
    console.error('Error processing MFA devices:', err)
    throw new Error('Failed to parse and normalize MFA device data')
  }
}

