  flex-shrink: 0;
  position: relative;
}

/* Message container for error and instructions */
.message-container {
  display: flex;
  flex-direction: column;
  gap: 24px;
  align-items: flex-start;
  justify-content: center;
  flex-shrink: 0;
  position: relative;
}

/* Container for text elements */
.header-text {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-12px, 12px);
  align-items: flex-start;
  justify-content: flex-start;
  flex-shrink: 0;
  width: 664px;
  position: relative;
}

/* Header text for the error message */
.error-message {
