  color: #000000;
  text-align: left;
  font-family: var(--body-body-default-font-family, "Roboto-Regular", sans-serif);
  font-size: var(--body-body-default-font-size, 16px);
  line-height: var(--body-body-default-line-height, 24px);
  font-weight: var(--body-body-default-font-weight, 400);
  position: relative;
  display: flex;
  align-items: flex-end;
  justify-content: flex-start;
}

/* Button container */
.button-container {
  display: flex;
  flex-direction: column;
  gap: 32px;
  align-items: flex-start;
  justify-content: center;
  flex-shrink: 0;
  position: relative;
}

/* Button styling */
.button {
  background: var(--ink-black, #000000);
  border-radius: var(--components-button-radius, 1000px);
  padding: 0px var(--spacing-24px, 24px);
  display: flex;
  flex-direction: row;
  gap: var(--spacing-12px, 12px);
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  height: 48px;
  position: relative;
  overflow: hidden;
}

/* Text within the button */
.button-text {
  color: var(--components-button-primary-text, #ffffff);
  text-align: center;
  font-family: var(--text-font-body-font, "Roboto-SemiBold", sans-serif);
  font-size: 16px;
  line-height: 24px;
  font-weight: 600;
  position: relative;
  display: flex;
  align-items: flex-end;
  justify-content: center;
}

/* Close icon */
