module.exports = a = async ({params}) => {
    console.log('params: ', params)

    try {
        const devices = params.input?.rawResponse?._embedded?.devices

        if (!Array.isArray(devices)) {
            throw new Error('Invalid input: rawResponse._embedded.devices must be an array')
        }

        const activeNotBlockedCount = devices.filter(device =>
            device.status === 'ACTIVE' &&
            device.block?.status !== 'BLOCKED'
        ).length

        if (activeNotBlockedCount === 0) {
            throw new Error('No active and unblocked devices found.')
        }

        return { activeNotBlockedCount }
    } catch (error) {
        console.error('Error processing devices:', error.message)
        throw new Error(`Device processing failed: ${error.message}`)
    }
}

