function checkInputs(showEmptyErrors = false) {
    let isValid = true;
    const currentVal = currentPassword.value.trim();
    const val = newPassword.value.trim();
    const confirmVal = confirmPassword.value.trim();


    if (currentVal === "") {
        currentPassword.classList.add("invalid");
        currentPasswordError.textContent = "Current password is required";
        currentPasswordError.style.display = "block";
        isValid = false;
    } else {
        currentPassword.classList.remove("invalid");
        currentPasswordError.style.display = "none";
    }

    // Password check
    if (val === "" && showEmptyErrors) {
        newPassword.classList.add("invalid");
        passwordError.textContent = "New password is required.";
        passwordError.style.display = "block";
        isValid = false;
    } else {
        let passwordValid = Object.values(rules).every((rule) =>
            rule.test(val)
        );
        if (!passwordValid) {
            newPassword.classList.add("invalid");
            passwordError.textContent = "Password does not meet requirements.";
            passwordError.style.display = "block";
            isValid = false;
        } else {
            newPassword.classList.remove("invalid");
            passwordError.style.display = "none";
        }
    }

    // Confirm password check
    if (confirmVal === "" && showEmptyErrors) {
        confirmPassword.classList.add("invalid");
        confirmPasswordError.textContent = "Please re-enter new password.";
        confirmPasswordError.style.display = "block";
        isValid = false;
    } else if (val !== confirmVal || confirmVal === "") {
        confirmPassword.classList.add("invalid");
        confirmPasswordError.textContent = "Passwords do not match.";
