function isAlphaNumericOrNumeric(input) {
  // Regular expression to check for alphanumeric or numeric input (without all alphabets)
  // const alphanumericRegex = /^[0-9a-zA-Z]+$/;
  const alphabetsOnlyRegex = /^[a-zA-Z]+$/;

  const numericRegex = /^\d+$/;

  return !alphabetsOnlyRegex.test(input) || numericRegex.test(input);
}

function showPassword(inputId, iconId) {
  var x = document.getElementById(inputId);
  var i = document.getElementById(iconId);
  if (x.type === 'password') {
    x.type = 'text';
    i.src =
