if (createAccountBtn) {
  createAccountBtn.disabled = true;
}

var createAccountEmailError = document.getElementById('create-account-email-address-error');
var createAccountPhoneNumberError = document.getElementById('create-account-phone-number-error');
var createAccountZipError = document.getElementById('create-account-zip-code-error');

function shouldEnableCreateAccount() {
  if (createAccountBtn) {
    if (
      createAccountEmailError.textContent === '' &&
      createAccountEmail.value &&
      createAccountPhoneNumberError.textContent === '' &&
      createAccountPhoneNumber.value &&
      createAccountZipError.textContent === '' &&
      createAccountZip.value
      // createAccountTermsAndService.checked
    ) {
      createAccountBtn.disabled = false;
    } else {
      createAccountBtn.disabled = true;
    }
  }
}

if (createAccountEmail) {
  createAccountEmail.addEventListener('input', function () {
    if (validateEmail(createAccountEmail.value)) {
      if (createAccountEmailError) {
        createAccountEmailError.textContent = '';
      }
      createAccountEmail.parentElement
        .querySelector('input[type="email"]')
        .classList.remove('login--custom-input-error');
      createAccountEmail.parentElement
        .querySelector('label[for="create-account-email-address"]')
        .classList.remove('login--custom-Label-error');
    } else {
      if (createAccountEmailError) {
        createAccountEmailError.textContent = 'Please enter a valid email address';
      }
      createAccountEmail.parentElement.querySelector('input[type="email"]').classList.add('login--custom-input-error');
      createAccountEmail.parentElement
        .querySelector('label[for="create-account-email-address"]')
        .classList.add('login--custom-Label-error');
    }
    shouldEnableCreateAccount();
  });
}

if (createAccountPhoneNumber) {
  createAccountPhoneNumber.addEventListener('input', function () {
    if (validatePhoneNumber(createAccountPhoneNumber.value)) {
      if (createAccountPhoneNumberError) {
        createAccountPhoneNumberError.textContent = '';
      }
      createAccountPhoneNumber.parentElement
        .querySelector('input[type="tel"]')
        .classList.remove('login--custom-input-error');
      createAccountPhoneNumber.parentElement
        .querySelector('label[for="create-account-phone-number"]')
        .classList.remove('login--custom-Label-error');
    } else {
      if (createAccountPhoneNumberError) {
        createAccountPhoneNumberError.textContent = 'Please enter a valid phone number';
      }
      createAccountPhoneNumber.parentElement
        .querySelector('input[type="tel"]')
        .classList.add('login--custom-input-error');
      createAccountPhoneNumber.parentElement
        .querySelector('label[for="create-account-phone-number"]')
        .classList.add('login--custom-Label-error');
    }
    shouldEnableCreateAccount();
  });
}

if (createAccountZip) {
  createAccountZip.addEventListener('input', function () {
    if (createAccountZip.value.trim().length > 0 && isAlphaNumericOrNumeric(createAccountZip.value)) {
      if (createAccountZipError) {
        createAccountZipError.textContent = '';
      }
      createAccountZip.parentElement.querySelector('input[type="text"]').classList.remove('login--custom-input-error');
      createAccountZip.parentElement
        .querySelector('label[for="create-account-zip-code"]')
        .classList.remove('login--custom-Label-error');
    } else {
      if (createAccountZipError) {
        createAccountZipError.textContent = 'Please enter a valid zip code';
      }
      createAccountZip.parentElement.querySelector('input[type="text"]').classList.add('login--custom-input-error');
      createAccountZip.parentElement
        .querySelector('label[for="create-account-zip-code"]')
        .classList.add('login--custom-Label-error');
    }
    shouldEnableCreateAccount();
  });
}

