/* Show custom select options start */

function showCustomSelectOptions() {
  var rotated = document.getElementById('angle-icon');
  var dropdownDiv = document.getElementById('custom-select-options');
  if (dropdownDiv.style.display === 'none') {
    dropdownDiv.style.display = 'block';
    rotated.style.transform = `rotate(180deg)`;
  } else {
    dropdownDiv.style.display = 'none';
    rotated.style.transform = `rotate(0deg)`;
  }
}

var selectOptions = document.querySelectorAll('#custom-select-options li');
var selectedValue = document.querySelector('#selected-value');
var selectedValueDiv = document.querySelector('#selected-value-div');
var selectLabel = document.querySelector('#custom-select-label');
var multipleAccountsBtn = document.querySelector('#register-multiple-accounts-continue');

if (multipleAccountsBtn) {
  multipleAccountsBtn.disabled = true;
}

if (selectOptions) {
  selectOptions.forEach((option) => {
    option.addEventListener('click', () => {
      selectedValue.value = option.textContent;
      selectedValueDiv.textContent = selectedValue.value;
      selectLabel.style.visibility = 'visible';
      selectLabel.style.opacity = '1';
      selectLabel.style.transitionDelay = '0.1s';
      if (selectedValue.value !== '') {
        multipleAccountsBtn.disabled = false;
      } else {
        multipleAccountsBtn.disabled = true;
      }
      showCustomSelectOptions();
    });
  });
}

var multi_accounts = document.querySelector('#register-multiple-accounts-click');
if (multi_accounts) {
  multi_accounts.addEventListener('click', () => {
    showCustomSelectOptions();
  });
}

var showPasswordIcon = document.querySelector('#password-icon');
if (showPasswordIcon) {
  showPasswordIcon.addEventListener('click', () => {
    showPassword('login-password', 'password-icon');
  });
}

var newPasswordIcon = document.querySelector('#new-password-icon');
if (newPasswordIcon) {
  newPasswordIcon.addEventListener('click', () => {
    showPassword('new-password', 'new-password-icon');
  });
}

var confirmPasswordIcon = document.querySelector('#confirm-password-icon');
if (confirmPasswordIcon) {
  confirmPasswordIcon.addEventListener('click', () => {
    showPassword('confirm-password', 'confirm-password-icon');
  });
}

// JavaScript to handle form submission when Enter key is pressed
document.getElementById('frmLogin').addEventListener('keypress', function (event) {
  if (event.keyCode === 13) {
    event.preventDefault(); // Prevent default form submission
    document.getElementById('login').click(); // Trigger the login button click
  }
});

