  position: absolute;
  top: 0;
  left: 0;
  width: 47.89%;
  height: 29.82%;
}

.enrolled-field-label {
  color: #000;
  font-family: "Roboto-SemiBold", sans-serif;
  font-size: 12px;
  line-height: 140%;
  letter-spacing: -0.019em;
  font-weight: 600;
  text-transform: uppercase;
  display: flex;
  align-items: flex-end;
  justify-content: flex-start;
  width: 100%;
  height: 100%;
}

/* ---------- Content Row ---------- */
.enrolled-content {
  display: flex;
  flex-direction: row;
  gap: 77px;
  align-items: flex-start;
  justify-content: flex-start;
  width: 100%;
  height: 42.11%;
  position: absolute;
  bottom: 14.04%;
  top: 43.86%;
}

/* ---------- Value ---------- */
.enrolled-value {
  color: #000;
  font-family: "Roboto-Regular", sans-serif;
  font-size: 16px;
  line-height: 24px;
  font-weight: 400;
  display: flex;
  align-items: flex-end;
  justify-content: flex-start;
  white-space: nowrap;
  /* overflow: hidden; */
  text-overflow: ellipsis;
  border:none
}

/* ---------- Edit Link ---------- */
.enrolled-edit-link {
  display: flex;
  flex-direction: row;
  gap: 4px;
  align-items: center;
  justify-content: flex-end;
  width: 83px;
  position: relative;
  left: 15px;
}

.edit-link {
  color: #000;
  font-family: "Roboto-SemiBold", sans-serif;
  font-size: 16px;
  line-height: 24px;
  font-weight: 600;
  text-decoration: underline;
}

/* ---------- Status Tag ---------- */
.enrolled-status {
  background: rgba(4, 135, 103, 0.15);
  border-radius: 4px;
  padding: 0 16px;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 88px;
  height: 28px;
}

.status-text {
  color: #048767;
  font-family: "Roboto-Bold", sans-serif;
  font-size: 10px;
  line-height: 18px;
  letter-spacing: 0.1em;
  font-weight: 700;
  text-transform: uppercase;
}

/* //////////////////////////////////////unenrolled Email//// */
.email-unenrolled,
.email-unenrolled * {
  box-sizing: border-box;
}

.email-unenrolled {
  display: flex;
  flex-direction: column;
  gap: 32px;
  align-items: flex-start;
  justify-content: flex-start;
  position: relative;
}

/* Email Section */
.email-section {
  display: flex;
  flex-direction: row;
  gap: 24px;
  align-items: flex-end;
  justify-content: flex-start;
  position: relative;
  margin-top: 30px;
}

/* Email Input Group */
.email-input-group {
  display: flex;
  flex-direction: column;
  gap: 0;
  align-items: flex-start;
  justify-content: flex-start;
  width: 280px;
  min-width: 250px;
  position: relative;
}

.email-label {
  color: #000;
  text-align: left;
  font-family: "Roboto-SemiBold", sans-serif;
  font-size: 12px;
  line-height: 140%;
  letter-spacing: -0.019em;
  font-weight: 600;
  text-transform: uppercase;
  margin-bottom: 4px;
}

.email-input-box {
  background: var(--components-form-input-fill-default, #ffffff);
  border-radius: var(--components-form-input-radius, 5px);
  border: 1px solid var(--components-form-input-border-default, #808080);
  /* padding: 12px 16px; */
  padding: 10px;
  display: flex;
  align-items: center;
  justify-content: flex-start;
  width:280px;
  min-width: 250px;
  min-height: 44px;
  overflow: hidden;
}

.email-address {
  color: var(--components-form-input-text-default, #000000);
  text-align: left;
  font-family: var(--body-body-default-font-family, "Roboto-Regular", sans-serif);
  font-size: var(--body-body-default-font-size, 16px);
  line-height: var(--body-body-default-line-height, 24px);
  font-weight: var(--body-body-default-font-weight, 400);
  text-overflow: ellipsis;
  /* overflow: scroll; */
  white-space: nowrap;
  border: none;
  outline: none;
  width:250px;
  /* ///////////////////////////////////////////////textarea// */
 /* width: 320px; */
  /* height: 44px;              looks like input */
  /* padding: 10px 12px; */
  /* border: 1px solid #ccc; */
  /* border-radius: 6px; */

  resize: none;              /* disable resize */
  white-space: nowrap;       /* force single line */
  overflow-x: hidden;        /* default: hide horizontal */
  overflow-y: hidden;        /* never show vertical */
  text-overflow: ellipsis;

  font-size: 16px;
  line-height: 24px;
  height: 30px;
  padding-left: 20px;
}
.email-address:hover,
.email-address:focus {
  overflow-x: auto;          /* horizontal scroll */
  text-overflow: clip;
  border-color: #4c7cff;
  /* overflow: scroll; */
  /* height: 40px;  */
}
.email-address::-webkit-scrollbar {
  height: 6px;              /* controls scrollbar thickness */
}

.email-address::-webkit-scrollbar-track {
  background: transparent; /* track background */
}

.email-address::-webkit-scrollbar-thumb {
  background-color: #9ca3af;
  border-radius: 10px;
}

.email-address::-webkit-scrollbar-thumb:hover {
  background-color: #6b7280;
}

/* Enroll Button */
.email-enroll-btn-wrapper {
  flex-shrink: 0;
  height: 48px;
  position: relative;
  margin-top: 20px;
}

.email-enroll-btn {
  background: var(--surface-primary, #ffffff);
  border-radius: var(--components-button-radius, 1000px);
  border: 1px solid var(--ink-black, #000000);
  padding: 0 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  height: 48px;
  width: 89px;
  overflow: hidden;
  cursor: pointer;
}

.email-enroll-text {
  color: var(--ink-black, #000000);
  text-align: center;
  font-family: var(--text-font-body-font, "Roboto-SemiBold", sans-serif);
  font-size: 16px;
  line-height: 24px;
  font-weight: 600;
  border:none;
  background-color: white;
}

