  /* Main dropdown container - hidden by default */
  .user-dropdown {
    display: none;
    position: absolute;
    top: calc(100% + 10px);
    right: 0;
    width: 240px;
    background: white;
    border-radius: 8px;
    box-shadow: 0px 6px 12px rgba(0, 0, 0, 0.25);
    z-index: 1000;
    padding: 8px 0;
    border: 1px solid #e0e0e0;
    overflow: hidden;
  }

  /* JS adds '.open' to #userIcon to show the dropdown */
  .profile-icon.open .user-dropdown {
    display: block;
  }

  /* "Select Language" trigger button */
  .user-dropdown .custom-select,
  .user-dropdown .custom-selectt {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 8px 12px;
    cursor: pointer;
    font-size: 14px;
    font-family: Roboto, sans-serif;
    font-weight: 400;
    line-height: 20px;
  }

  /* Arrow icon within the trigger */
  .user-dropdown .arrow-down {
    font-size: 20px;
    font-weight: 900;
    color: black;
  }

  /* Language options list (sub-menu) - hidden by default */
  .user-dropdown .dropdown-options,
  .user-dropdown .dropdown-optionss {
    list-style: none;
    padding: 0;
    margin: 0;
    display: none; /* Hidden until its trigger is open */
  }

  /* JS adds '.open' to #mobileLanguageTrigger to show the languages */
  .user-dropdown .custom-select.open + .dropdown-options,
  .user-dropdown .custom-selectt.open + .dropdown-optionss {
    display: block;
  }

  /* Individual language item */
  .user-dropdown .dropdown-options li,
  .user-dropdown .dropdown-optionss li {
    padding: 8px 24px; /* Indented padding from guideline */
    cursor: pointer;
    font-size: 14px;
    font-weight: 400;
    line-height: 20px;
  }

  .user-dropdown .dropdown-options li:hover,
  .user-dropdown .dropdown-optionss li:hover {
    background-color: #f0f0f0;
  }

  /* Separator line */
  .user-dropdown hr {
    border: none;
    border-top: 1px solid #e0e0e0;
    /* margin: 8px 0; */
  }
