function setupEditHandlers() {
  const phoneEditLink = document.querySelector(".phone-enrolled .edit-link");
  const emailEditLink = document.querySelector(".email-enrolled .text-link");

  // --- PHONE EDIT ---
  phoneEditLink?.addEventListener("click", (e) => {
    e.preventDefault();

    document.querySelector(".phone-enrolled").style.display = "none";
    document.querySelector(".phone-unenrolled").style.display = "block";

    const phoneInput = document.getElementById("phone-unenrolled-input");
    if (phoneInput) {
      phoneInput.removeAttribute("readonly");
      phoneInput.focus();
      // phoneInput.addEventListener("input", setupValidation);
      // phoneInput.addEventListener("blur", setupValidation);
      phoneInput.setSelectionRange(phoneInput.value.length, phoneInput.value.length);
      setupValidation();
    }
  });

  // --- EMAIL EDIT ---
  emailEditLink?.addEventListener("click", (e) => {
    e.preventDefault();

    document.querySelector(".email-enrolled").style.display = "none";
    document.querySelector(".email-unenrolled").style.display = "block";

    const emailInput = document.getElementById("email-unenrolled-input");
    if (emailInput) {
      emailInput.removeAttribute("readonly");
      emailInput.focus();
      // emailInput.setSelectionRange(emailInput.value.length, emailInput.value.length);
      setupValidation();
    }
  });

  // --- AUTH APP EDIT ---
  const authEditBtn = document.getElementById("authAppEditBtn");
  const authSaveBtn = document.getElementById("authAppSaveBtn");
  const authInputNew = document.getElementById("auth-app-enrolled-input");

  authEditBtn?.addEventListener("click", () => {
    if (!authInputNew) return;

    authInputNew.removeAttribute("readonly");
    authInputNew.focus();

    authEditBtn.style.display = "none";
    authSaveBtn.style.display = "inline-block";
  });

  authSaveBtn?.addEventListener("click", () => {
    if (!authInputNew) return;

    authInputNew.setAttribute("readonly", true);
    console.log("Saved Auth App Name:", authInputNew.value);

    authEditBtn.style.display = "inline-block";
    authSaveBtn.style.display = "none";
  });


}



