  //  const domain = "grandresidenceclub.com";
  const domain = "{{global.variables.domain}}";
  let contactLink = "";
     if (profileLink) {
      profileLink.style.display = "inline-block";
      forAllDomainLink.style.display ="block"
    }
     if (spanlink) {
      spanlink.style.display = 'none';
    }
 
  // --- Determine which domain we're on ---
  if (domain.includes("grandresidenceclub.com")) {
    // GRC domain
  
    // profileLink.style.display=
    // contactLink ="{{global.company.variables.GRCContactInformationPage}}";
    // console.log("Current domain: GRC");
   
