function setContactLinkOwnerServiceByDomain() {
  const contactLink = document.getElementById("contactOwner");
  const contactOwnerdeckstop = document.getElementById('contactOwnerdeckstop')
  if (!contactLink) return;

  const domain = "{{global.variables.domain}}";
  // Get cookie value by name
  function getCookie(name) {
    const cookies = document.cookie.split("; ");
    for (let c of cookies) {
      const [key, value] = c.split("=");
      if (key === name) return value;
    }
    return null;
  }

  // const langPref = getCookie("langPref");
  const langPref = getCookie("langPref") || getCookie("selectedLanguage");

  console.log('next Lng selected', langPref)

  // Apply href based on domain
  if (
    domain.includes("marriottvacationclub") ||
    domain.includes("grandresidenceclub")
  ) {
    // MVC & GRC
    contactLink.href =
      "{{global.variables.contactUsLink}}";

   contactOwnerdeckstop.href =
      "{{global.variables.contactUsLink}}";
      
  } 
  else if (domain.includes("vacationclubap")) {
    // VCAP
    contactLink.href =
      "{{global.variables.contactUsLink}}" + langPref;

    contactOwnerdeckstop.href =
      "{{global.variables.contactUsLink}}" + langPref;
  } 
  else if (domain.includes("ritzcarltonclub")) {
    // RCDC
    contactLink.href =
      "{{global.variables.contactUsLink}}";
    contactOwnerdeckstop.href =
      "{{global.variables.contactUsLink}}";
  }
}

const cancelAuthBtn = document.getElementById("cancleAuthChanges");
const authAppInput = document.getElementById("auth-app-enrolled-input"); 
 

let defaultAuthAppName = authAppInput.value;


function cancelAuthAppAction() {
  const authAppInput = document.getElementById("auth-app-enrolled-input"); 
  const errorMessageDivSS = document.getElementById("errorMessageDivSS");

  if (!authAppInput) return;

  errorMessageDivSS.style.display = "flex";
  setTimeout(() => {
    errorMessageDivSS.style.display = "none";
  }, 5000);

  authAppInput.value = defaultAuthAppName;
}



if (cancelAuthBtn) {
  cancelAuthBtn.addEventListener("click", cancelAuthAppAction);
}

