function validatePhone(event) {
  const phoneInput = document.getElementById("phone-unenrolled-input");
  const inputTelephone = document.getElementById("inputTelephone");
  const phoneError = document.getElementById("phoneError");

  // ðŸ”’ Safety check (prevents classList null error)
  if (!phoneInput || !inputTelephone || !phoneError) {
    return true;
  }

 if(phoneInput.value === ""){
   console.log('event is stop due to phone empty');
   event.preventDefault();
   event.stopPropagation();
   return false;
 }
  const value = phoneInput.value || "";

  // Only digits
  const digitsOnly = value.replace(/\D/g, "");

  // Checks
  const isCorrectLength = /^[0-9]{7,17}$/.test(digitsOnly);
  const hasNonDigits = /[^0-9]/.test(value);

  // Reset UI
  phoneError.style.display = "none";
  inputTelephone.classList.remove("error");

  // 1) Length check (7â€“17 digits)
  if (!isCorrectLength) {
    phoneError.innerText =
      "Please enter a valid phone number (7â€“17 digits).";
    phoneError.style.display = "block";
    inputTelephone.classList.add("error");
    return false;
  }

  // 2) No letters or special characters
  if (hasNonDigits) {
    phoneError.innerText = "Please enter a valid phone number.";
    phoneError.style.display = "block";
    inputTelephone.classList.add("error");
    return false;
  }

  // 3) Valid number
  phoneError.style.display = "none";
  inputTelephone.classList.remove("error");
  return true;
}


