  function init() {
  renderOptions();

  if (!countryData.length) return;

  let selectedCountry = null;

 
  // 1. Match by dial code (+91, +1, etc.)
  if (defaultCountryCode) {
    const normalizedCode = defaultCountryCode.replace("+", "");

    selectedCountry = countryData.find(
      c => c.dialCode.replace("+", "") === normalizedCode
    );
  }

  // 2. Fallback to US
  if (!selectedCountry) {
    selectedCountry = countryData.find(
      c => c.iso2.toUpperCase() === "US"
    );
  }

  // 3. Final fallback to first item
  if (!selectedCountry) {
    selectedCountry = countryData[0];
  }

  selectOption(selectedCountry);
}

  function toggleDropdown() {
    optionsContainer.classList.toggle('active');
    selectBox.classList.toggle('open');
  }
  document.querySelector('.select-box').addEventListener('click', toggleDropdown )

  function selectOption(country) {
    // 1. CLOSED VIEW: Only Flag + Code
    const phoneInput = document.getElementById("phone-unenrolled-input");

    selectedValue.innerHTML = `
      <img src="${country.flag}" alt="${country.iso2}" class="flag-img">
      <span class="dial-code">${country.iso2}</span>
    `;

    // 2. Set Value
    hiddenInput.value = country.dialCode;
    hiddenInput.setAttribute("value", country.dialCode);

    // 3. Highlight selected in list
    document.querySelectorAll('.option').forEach(el => el.classList.remove('selected'));
    const selectedOption = document.querySelector(`.option[data-iso="${country.iso2}"]`);
    if(selectedOption) selectedOption.classList.add('selected');

    if (phoneInput) {
    const currentValue = phoneInput.value || "";

    // Remove existing dial code if present
    const cleanedNumber = currentValue.replace(/^\+\d+\s*/, "");

    phoneInput.value = cleanedNumber;   // `${country.dialCode} ${cleanedNumber}`.trim();
  }

    // 4. Close
    optionsContainer.classList.remove('active');
    selectBox.classList.remove('open');
  }

  function renderOptions() {
  optionsList.innerHTML = "";

  countryData.forEach(country => {
    const optionDiv = document.createElement("div");
    optionDiv.className = "option";
    optionDiv.dataset.iso = country.iso2;

    optionDiv.innerHTML = `
      <img src="${country.flag}" class="flag-img">
      <span class="country-name">${country.name}</span>
      <span class="dial-code">${country.dialCode}</span>
    `;

    optionDiv.addEventListener("click", (e) => {
      e.stopPropagation();        // ðŸ”‘ VERY IMPORTANT
      selectOption(country);
    });

    optionsList.appendChild(optionDiv);
  });
}


