.auth-app-enrolled * {
  box-sizing: border-box;
}

.auth-app-enrolled {
  width: 100%;
  position: relative;
}

/* ---- Container ---- */
.auth-readonly-container {
  position: relative;
  height: 120px;
}

/* ---- Label Section ---- */
.auth-field-header {
  position: relative;
  top: 25px;
  left: 0;
  width: 47.89%;
  height: 29.82%;
}

.auth-field-label {
  color: #000;
  font-family: "Roboto-SemiBold", sans-serif;
  font-size: 12px;
  line-height: 140%;
  letter-spacing: -0.019em;
  font-weight: 600;
  text-transform: uppercase;
  display: flex;
  align-items: flex-end;
  justify-content: flex-start;
  height: 100%;
}

/* ---- Content Row ---- */
.auth-field-content {
  display: flex;
  flex-direction: row;
  align-items: flex-start;
  justify-content: space-between;
  position: relative;
  top: 5%;
  bottom: 14.04%;
  width: 100%;
  height: 42.11%;
  margin-bottom: 8px;
}

