  color: #000;
  font-family: "Roboto-Regular", sans-serif;
  font-size: 16px;
  line-height: 24px;
  font-weight: 400;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  display: flex;
  align-items: flex-end;
  border:none
}

/* ---- Actions ---- */
.auth-actions {
  display: flex;
  flex-direction: row;
  align-items: center;
  gap: 24px;
}

.auth-link {
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: flex-end;
}

.auth-link-text {
  color: #000;
  font-family: "Roboto-SemiBold", sans-serif;
  font-size: 16px;
  line-height: 24px;
  font-weight: 600;
  text-decoration: underline;
  cursor: pointer;
  border: none;
  background-color: white;
}

.auth-divider {
  color: #000;
  font-family: "Roboto-SemiBold", sans-serif;
  font-size: 16px;
  line-height: 24px;
