function openSecuritySectionOnLoad() {
    const manageAccountFlag = sessionStorage.getItem("manageAccountFlag") === "true";
    if (!manageAccountFlag) return;
    console.log("manageAccountFlag: ",manageAccountFlag);
    const headerSecurity = document.getElementById("security-setting-id-header");
    const contentSecurity = document.querySelector(".accordion-content-security-manage-account");
    const arrow = document.getElementById("desktopArrowSecurity"); // âœ… from your HTML
 
    if (headerSecurity && contentSecurity && arrow) {
        contentSecurity.classList.add("open");
        headerSecurity.classList.add("active");
        arrow.classList.add("open");
 
        contentSecurity.scrollIntoView({ behavior: "smooth" });
    }
}
openSecuritySectionOnLoad();

function checkAnyVerified() {
  let isPhone = json.phone?.status === "verified";
  let isEmail = json.email?.status === "verified";
  let isAuth = Array.isArray(json.totpDevices) &&
    json.totpDevices[0]?.status === "verified";

  const errorMsg = document.getElementById("errorMessageDivForEnroll");
  const haveAuthApp = document.getElementById("haveAuthApp");

  if (isPhone || isEmail || isAuth) {
    // ANY verified â†’ hide error
    errorMsg.style.display = "none";
    haveAuthApp.style.display = "block";
  } else {
    // ALL not-verified â†’ show error
    errorMsg.style.display = "flex";
    haveAuthApp.style.display = "none";
  }
}
