let defaultValue=''
function initEditableField(inputId, toggleId) {
  const input = document.getElementById(inputId);
  const toggleLink = document.getElementById(toggleId);
  const saveBtn = document.getElementById("saveBtn");
  // const errorMessageDiv = document.getElementById("errorMessageDiv");
  const saveMessageDiv = document.getElementById("masageSuccess");

  let originalValue = input.value;
  let saveMessageTimer = null;
  let errorMessageTimer=null;

  function forceFocus(e) {
    if (!input.hasAttribute("readonly")) {
      e.preventDefault();
      // setTimeout(() => {
      input.focus();
      const len = input.value.length;
      input.setSelectionRange(len, len);
      // }, 0);
    }
  }


  // ðŸ‘‰ Handle Edit / Cancel Changes toggle
  toggleLink.addEventListener("click", function (e) {
   e.preventDefault();


    if (input.hasAttribute("readonly")) {
      input.removeAttribute("readonly");
      // input.focus();
      input.addEventListener("click", forceFocus);
      input.focus();
      input.setSelectionRange(input.value.length, input.value.length);

      toggleLink.textContent = "Cancel Changes";
      toggleLink.classList.add("cancel-button");
      toggleLink.classList.remove("action-link-text");

      saveBtn.classList.remove("hide");
      saveBtn.classList.add("dsblock");
      saveBtn.style.display = "block";

      // Hide both messages while editing
       errorMessageDiv.style.display = "none";
      saveMessageDiv.style.display = "none";
      savedefaultValue(input);

    } else {
      // Cancel changes: revert to last saved value
      input.setAttribute("readonly", true);
      console.log(defaultValue)
      input.value = defaultValue;

      toggleLink.textContent = "Edit";
      toggleLink.classList.remove("cancel-button");
      toggleLink.classList.add("action-link-text");

      saveBtn.style.display = "none";

      // Show error message (cancel case)
       errorMessageDiv.style.display = "flex";
      saveMessageDiv.style.display = "none";

      
      if (errorMessageTimer) clearTimeout(errorMessageTimer);
      errorMessageTimer = setTimeout(() => {
        errorMessageDiv.style.display = "none";
      },5000);
    }
  });

  // ðŸ‘‰ Handle Save button click
  saveBtn.addEventListener("click", function () {
   // const errorMsgExistUsername = document.getElementById('error-msg');
    input.setAttribute("readonly", true);

    //   // Save whatever user entered
    originalValue = input.value;


    toggleLink.textContent = "Edit";


    toggleLink.classList.remove("cancel-button");
    toggleLink.classList.add("action-link-text");

    saveBtn.style.display = "none";

    // // Hide error and show success
     errorMessageDiv.style.display = "none";
    // saveMessageDiv.style.display = "flex";

    // Clear any existing timer
    if (saveMessageTimer) clearTimeout(saveMessageTimer);

    // Hide success after 1s
    saveMessageTimer = setTimeout(() => {
      saveMessageDiv.style.display = "none";
    }, 5000);
    
    setTimeout(() => {
     const errorMsgs = document.querySelectorAll('#error-msg');
      errorMsgs.forEach(msg => {
       msg.style.display = 'none';
      });
    }, 5000);

   });
}

