module.exports = a = async ({params}) => {
    console.log('params: ', params)

    try {
        const token = params.input
        const parts = token.split('.')
        if (parts.length !== 3) {
            throw new Error('Invalid JWT token format')
        }

        // Decode header
        const header = JSON.parse(decodeBase64Url(parts[0]))
        const kid = header.kid
        if (!kid) {
            throw new Error('Key ID (kid) not found in token header')
        }

        // Decode payload
        const payload = JSON.parse(decodeBase64Url(parts[1]))
        const issuer = payload.iss
        if (!issuer) {
            throw new Error('Issuer (iss) claim not found in token payload')
        }

        let env = payload.env

        if (!env) {
            // Try extracting from Cognito-style issuer URL
            const match = issuer.match(/cognito-idp\.[^\/]+\.amazonaws\.com\/([^\/]+)/)
            if (match && match[1]) {
                env = match[1]
            }
        }

        if (!env) {
            throw new Error('Environment (env) could not be determined from token')
        }

        // Extract domain from issuer
        const issuerMatch = issuer.match(/^https?:\/\/([^\/]+)/)
        if (!issuerMatch || !issuerMatch[1]) {
            throw new Error('Failed to extract hostname from issuer URL')
        }

        const hostname = issuerMatch[1]
        const domainParts = hostname.split('.')
        if (domainParts.length < 2) {
            throw new Error('Invalid domain format in issuer')
        }

        const domain = domainParts.slice(-2).join('.') // e.g., pingone.com or amazonaws.com

        return { issuerDomain: domain, keyId: kid, env: env }

    } catch (error) {
        console.error('Error processing JWT token:', error.message)
        throw new Error(`Failed to process JWT token: ${error.message}`)
    }
}


function decodeBase64Url(base64url) {
    // Replace URL-safe characters and pad with '='
    const base64 = base64url.replace(/-/g, '+').replace(/_/g, '/')
        .padEnd(Math.ceil(base64url.length / 4) * 4, '=');
    return Buffer.from(base64, 'base64').toString('utf8');
}
