module.exports = a = async ({params}) => {
    console.log('params: ', params)

    try {
        if (!params.input) {
            throw new Error("Missing input")
        }

        const parsedInput = typeof params.input === 'string' ? JSON.parse(params.input) : params.input;

        if (!parsedInput.rawResponse) {
            throw new Error("Missing rawResponse in input")
        }

        const rawResponse = JSON.parse(parsedInput.rawResponse);

        if (!rawResponse.body) {
            throw new Error("Missing body in rawResponse")
        }

        const body = JSON.parse(rawResponse.body);
        const pingUser = body.pingUser;

        return {
            username: pingUser.username,
            preferredUserName: pingUser.preferredUsername,
            name: pingUser.name,
            enabled: pingUser.enabled,
            ownerStatus: pingUser.ownerStatus,
            newOwnerStatus: pingUser.newOwnerStatus,
            ownerCustomerId: pingUser.ownerCustomerId,
            ownerFirstName: pingUser.ownerFirstName,
            ownerLastName: pingUser.ownerLastName,
            primaryPhone: pingUser.phoneNumber,
            isPhoneNumberVerified: pingUser.isPhoneNumberVerified,
            email: pingUser.email,
            isEmailVerified: pingUser.isEmailVerified,
            cognitoSub: pingUser.cognitoSub,
            createdAt: pingUser.createdAt,
            userStatus: pingUser.userStatus,
            cognitoConfirmationStatus: pingUser.cognitoConfirmationStatus,
            password: pingUser.password,
            population: pingUser.population
        }

    } catch (err) {
        console.error('Error parsing input: ', err)
        throw new Error(`Failed to parse input: ${err.message}`)
    }
}

