// ========================================================================
//                             Configuration
// ========================================================================

// --- Unique IDs for our dynamic elements ---
const BACKGROUND_IFRAME_ID = 'dynamic-background-iframe';
const BACKGROUND_OVERLAY_ID = 'dynamic-background-overlay';

const MOXIE_SCRIPT_ID = 'moxie-translation-script';
const domain = "{{global.variables.domain}}";
console.log("domain  is:", domain);

function getMoxieKey(domain) {
    switch (domain) {
        case "marriottvacationclub.com":
            return "{{global.company.variables.moxieKeyMVC}}";
        case "vacationclubap.com":
            return "{{global.company.variables.moxieKeyVCAP}}";
        default:
            // console.warn(`Unknown domain '${domain}'. Falling back to MVC key.`);
            console.warn(`Unknown domain '${domain}'. Moxie key left empty.`);
            return "";
          
    }
}
const moxieKey =  getMoxieKey(domain);
console.log("Moxie Key is:", moxieKey);




// --- Global flag to prevent re-execution across SPA navigations ---
// If this script has already run once, stop immediately.
if (window.myCustomWelcomeScriptHasRun) {
    // Script already initialized. Aborting secondary execution.
} else {

    // Set the flag to true immediately to prevent this block from ever running again.
    window.myCustomWelcomeScriptHasRun = true;

    // ========================================================================
    //                             Helper Functions
    // ========================================================================

    /**
     * Creates and injects a dimmed, full-screen iframe background.
     */
    function insertBackgroundSiteDimmed(url) {
        const targetDoc = window.top.document;
        if (targetDoc.getElementById(BACKGROUND_IFRAME_ID)) return;

        console.log("Injecting dimmed background...");

        const overlay = targetDoc.createElement('div');
        overlay.id = BACKGROUND_OVERLAY_ID;
        overlay.style.cssText = 'position:fixed; top:0; left:0; width:100vw; height:100vh; background:rgba(0, 0, 0, 0.8); z-index:-1;';

        const iframe = targetDoc.createElement('iframe');
        iframe.id = BACKGROUND_IFRAME_ID;
        iframe.src = url;
        iframe.style.cssText = 'position:fixed; top:0; left:0; width:100vw; height:100vh; border:none; z-index:-2;';
        iframe.setAttribute('sandbox', 'allow-scripts allow-same-origin');

        targetDoc.body.insertBefore(overlay, targetDoc.body.firstChild);
        targetDoc.body.insertBefore(iframe, targetDoc.body.firstChild);
    }

    /**
     * Finds and removes the background iframe and its overlay.
     */
    function removeBackgroundSiteDimmed() {
        const targetDoc = window.top.document;
        const iframe = targetDoc.getElementById(BACKGROUND_IFRAME_ID);
        const overlay = targetDoc.getElementById(BACKGROUND_OVERLAY_ID);

        if (iframe) iframe.remove();
        if (overlay) overlay.remove();
    }





    /**
     * Injects the Moxie script. We let the script itself handle language detection.
     */
    function injectMoxieScript() {
        const targetDoc = window.top.document;
        if (targetDoc.getElementById(MOXIE_SCRIPT_ID)) return; // Safety check

        console.log("Injecting Moxie translation script.");
        const moxieScript = targetDoc.createElement("script");
        moxieScript.id = MOXIE_SCRIPT_ID;
        moxieScript.src = "https://www.onelink-edge.com/moxie.min.js";
        moxieScript.setAttribute("data-oljs", moxieKey);
        targetDoc.head.appendChild(moxieScript);
    }

    /**
     * Hides the modal container AND removes the background elements.
     */
    function closeModalAndBackground() {
        const modalContainer = document.getElementById('welcomeModal');
        if (modalContainer) {
            modalContainer.style.display = 'none';
        }
        removeBackgroundSiteDimmed();
    }

    // ========================================================================
    //                     Initialization Logic
    // ========================================================================
    console.log("Custom script initializing for the first time.");

    // 1. Load the background immediately.

    if (domain.includes("grandresidenceclub.com")) {

        insertBackgroundSiteDimmed("{{global.company.variables.backGroundImageGRCUrl}}");
        backgroundVisible = true;

    } else if (domain.includes("myritzcarltonclub.com")) {
        insertBackgroundSiteDimmed("{{global.company.variables.backGroundImageRCCUrl}}");
        backgroundVisible = true;
    }
    else {
        insertBackgroundSiteDimmed("{{global.company.variables.backGroundImageUrl}}");
        backgroundVisible = true;
    }

    // 2. Load the translation script, trusting it to handle its own logic.
    injectMoxieScript();

    // 3. Set up a listener for the modal close buttons.
    window.top.document.body.addEventListener('click', function (event) {
        if (event.target.closest('#closeBtn') || event.target.closest('#modalX')) {
            closeModalAndBackground();
        }
    });

} // End of the main "if" block

