// ==========================
// DOM Elements
// ==========================
const firstNameInput = document.getElementById('first-name');
const lastNameInput = document.getElementById('last-name');
const ownerIdInput = document.getElementById('owner-id');
const findUserBtn = document.getElementById("findUserBtn");
//const customAlertMessage = document.querySelector(".alert-box");

const firstNameError = document.getElementById('first-name-error');
const lastNameError = document.getElementById('last-name-error');
const ownerIdError = document.getElementById('owner-id-error');
const MOXIE_SCRIPT_ID = "moxie-translation-script";
const domain = "{{global.variables.domain}}";
console.log("testing domain name: " + domain);
const moxieKey = getMoxieKey(domain);
console.log("Moxie Key is:", moxieKey);



// Choose correct Moxie key based on domain

function getMoxieKey(domain) {
    switch (domain) {
        case "marriottvacationclub.com":
            return "{{global.company.variables.moxieKeyMVC}}";
        case "vacationclubap.com":
            return "{{global.company.variables.moxieKeyVCAP}}";
        default:
            // console.warn(`Unknown domain '${domain}'. Falling back to MVC key.`);
            console.warn(`Unknown domain '${domain}'. Moxie key left empty.`);
            return "";

    }
}

// ==========================
// State
// ==========================
let touchedFields = {
    firstName: false,
    lastName: false,
    ownerId: false
};


// =============================================================================================================new js====
// ------------------ Config ------------------




// --- NEW: Unique IDs for our dynamic elements ---
const BACKGROUND_IFRAME_ID = 'dynamic-background-iframe';
const BACKGROUND_OVERLAY_ID = 'dynamic-background-overlay';



// Track whether background is currently active
let backgroundVisible = false;

// ==========================
// Validation Functions
// ==========================

function showError(inputElement, errorElement, message) {
    inputElement.parentElement.classList.add('invalid');
    errorElement.textContent = message;
    errorElement.style.display = 'block';
}

function hideError(inputElement, errorElement) {
    inputElement.parentElement.classList.remove('invalid');
    errorElement.textContent = '';
    errorElement.style.display = 'none';
    // customAlertMessage.style.display = 'none';
}

function validateField(inputElement, errorElement, message, touched) {
    if (!touched) return true;

    const isValid = inputElement.value.trim() !== '';

    if (!isValid) {
        showError(inputElement, errorElement, message);
    } else {
        hideError(inputElement, errorElement);
    }

    return isValid;
}

function validateFields() {
    const isFirstNameValid = validateField(firstNameInput, firstNameError, 'Please enter your First Name.', touchedFields.firstName);
    const isLastNameValid = validateField(lastNameInput, lastNameError, 'Please enter your Last Name.', touchedFields.lastName);
    const isOwnerIdValid = validateField(ownerIdInput, ownerIdError, 'Please enter your Customer ID.', touchedFields.ownerId);

    return isFirstNameValid && isLastNameValid && isOwnerIdValid;
}

// ==========================
// Event Handlers
// ==========================

function handleBlur(fieldKey) {
    touchedFields[fieldKey] = true;
    validateFields();
}

function attachEventListeners() {
    firstNameInput.addEventListener('blur', () => handleBlur('firstName'));
    lastNameInput.addEventListener('blur', () => handleBlur('lastName'));
    ownerIdInput.addEventListener('blur', () => handleBlur('ownerId'));

    firstNameInput.addEventListener('input', validateFields);
    lastNameInput.addEventListener('input', validateFields);
    ownerIdInput.addEventListener('input', validateFields);

    const form = document.getElementById('findUserBtn');
    if (form) {
        form.addEventListener('submit', handleFormSubmit);
    }
}

function handleFormSubmit(event) {
    event.preventDefault(); // Prevent form from actually submitting

    // Mark all fields as touched
    touchedFields.firstName = true;
    touchedFields.lastName = true;
    touchedFields.ownerId = true;

    const isFormValid = validateFields();

    if (isFormValid) {
        console.log('Form is valid!');
        // Proceed to submit or next step
    }
}

// ==========================
// Utility
// ==========================
// function closeModal() {
//     document.querySelector('.overlay').style.display = 'none';
// }

// ==========================
// Init
// ==========================
function initFormValidation() {
    attachEventListeners();
}

// Start validation setup
initFormValidation();




/**
 * Creates and injects a dimmed, full-screen iframe background.
 * Checks if elements already exist to avoid duplicates.
 * @param {string} url - The URL for the background website.
 */
function insertBackgroundSiteDimmed(url) {
    if (document.getElementById(BACKGROUND_IFRAME_ID)) return;

    // Create the dimming overlay
    const overlay = document.createElement('div');
    overlay.id = BACKGROUND_OVERLAY_ID;
    overlay.style.cssText = `
      position:fixed; top:0; left:0; width:100vw; height:100vh;
      background:rgba(0,0,0,0.8); z-index:-1;
    `;

    // Create the iframe
    const iframe = document.createElement('iframe');
    iframe.id = BACKGROUND_IFRAME_ID;
    iframe.src = url;
    iframe.style.cssText = `
      position:fixed; top:0; left:0; width:100vw; height:100vh;
      border:none; z-index:-2;
    `;
    iframe.setAttribute('sandbox', 'allow-scripts allow-same-origin');

    // Add them to the page
    document.body.insertBefore(overlay, document.body.firstChild);
    document.body.insertBefore(iframe, document.body.firstChild);

    console.log("Dimmed background and iframe have been injected.");
}


// --- 1. Main Logic to Control the Background ---
const manageBackgroundVisibility = () => {
    const modal = document.getElementById('findUserModal');

    if (modal && !backgroundVisible) {

        if (domain.includes("grandresidenceclub.com")) {
            insertBackgroundSiteDimmed("{{global.company.variables.backGroundImageGRCUrl}}");
            backgroundVisible = true;

        } else if (domain.includes("myritzcarltonclub.com")) {
            insertBackgroundSiteDimmed("{{global.company.variables.backGroundImageRCCUrl}}");
            backgroundVisible = true;
        }
        else {
            insertBackgroundSiteDimmed("{{global.company.variables.backGroundImageUrl}}");
            backgroundVisible = true;
        }
    }
    // else if (!modal && backgroundVisible) {
    //   removeBackgroundSiteDimmed();
    //   backgroundVisible = false;
    // }
}

// --- 2. Watch for Page Changes in the SPA ---
const observer = new MutationObserver(manageBackgroundVisibility);
observer.observe(document.body, { childList: true, subtree: true });

// --- 3. Initial Setup ---
manageBackgroundVisibility();

// --- 4. Language Preference Helper ---
function getLanguagePreference() {
    const langCookie = document.cookie.split('; ')
        .find(row => row.startsWith('langPref='));
    return langCookie ? langCookie.split('=')[1] : 'en';
}

// --- 5. Handle Moxie Script Injection ---
function initLanguageOnLoad() {
    // if (!dev) {
    console.log("PRODUCTION MODE: Injecting Moxie translation script.");
    const moxieScript = document.createElement("script");
    moxieScript.src = "https://www.onelink-edge.com/moxie.min.js";
    moxieScript.type = "text/javascript";
    moxieScript.setAttribute("data-oljs", moxieKey);
    document.head.appendChild(moxieScript);
    // } else {
    //     console.log('Language pref is: ', getLanguagePreference());
    // }
}

initLanguageOnLoad();


function applyMoxieTranslations() {
    const existingScript = document.getElementById(MOXIE_SCRIPT_ID);
    if (existingScript) existingScript.remove();

    const moxieScript = document.createElement("script");
    moxieScript.id = MOXIE_SCRIPT_ID;
    moxieScript.src = "https://www.onelink-edge.com/moxie.min.js";
    moxieScript.type = "text/javascript";
    moxieScript.setAttribute("data-oljs", moxieKey);
    moxieScript.onerror = () => console.error("CRITICAL: Moxie script failed to load.");
    document.head.appendChild(moxieScript);
}
applyMoxieTranslations();


function handleFindUserBtnClick(event) {
    const firstName = document.getElementById("first-name").value.trim();
    const lastName = document.getElementById("last-name").value.trim();
    const ownerId = document.getElementById("owner-id").value.trim();
    const errorMsg = document.getElementById("errorMsg");
    
    console.log("button clicked")
    if (firstName === "" || lastName === "" || ownerId === "") {
        console.log("Enetr data")
        event.preventDefault(); // Stop form from submitting
        event.stopPropagation();
        errorMsg.textContent = "Please enter details";
        errorMsg.style.color = "#eb0000";
        errorMsg.style.fontFamily = "Roboto-Regular, sans-serif";
        errorMsg.style.textAlign = "left";

        errorMsg.style.fontSize = "12px";
        errorMsg.style.lineHeight = "18px";
        errorMsg.style.fontWeight = "400";
        errorMsg.style.display = "block";
        // return false;
    }
    else {
        console.log("Else button")
        errorMsg.textContent = "";
        errorMsg.style.display = "none";
        // return true;
    }
}
function clearErrorOnInput() {
    const errorMsg = document.getElementById("errorMsg");
    errorMsg.textContent = "";
    errorMsg.style.display = "none";
}
document.getElementById("findUserBtn").addEventListener("click", handleFindUserBtnClick);
