customCSS.value:
 * {
   box-sizing: border-box;
   margin: 0;
   padding: 0;
 }

 body {
   font-family: "Roboto", sans-serif;
   background-color: rgba(0, 0, 0, 0.4);
   display: flex;
   justify-content: center;
   align-items: center;
   height: 100vh;
 }

 .modal {
   background-color: white;
   border-radius: 10px;
   padding: 48px;
   width: 90%;
   max-width: 760px;
   min-width: 352px;
   box-shadow: 0 3px 10px rgba(0, 0, 0, 0.3);
   position: relative;
   display: flex;
   flex-direction: column;
   gap: 24px;
 }

 .modal h2 {
   font-size: 20px;
   font-weight: 600;
   color: #000;
 }

 .modal p {
   font-size: 16px;
   color: #000;
   line-height: 1.5;
 }

 .form-group {
   display: flex;
   flex-direction: column;
   /* gap: 8px; */
 }

 .form-group label {
   font-size: 14px;
   font-weight: 600;
   text-transform: uppercase;
   color: #000;
 }

 .form-group input {
   background: var(--components-form-input-fill-default, #ffffff);
   border-radius: var(--components-form-input-radius, 5px);
   border-style: solid;
   border-color: var(--components-form-input-border-default, #808080);
   border-width: 1px;
   padding: var(--spacing-12px, 12px) var(--spacing-16px, 16px) var(--spacing-12px, 12px) var(--spacing-16px, 16px);
   display: flex;
   flex-direction: row;
   gap: var(--spacing-12px, 12px);
   align-items: center;
   justify-content: center;
   align-self: stretch;
   flex-shrink: 0;
   min-width: 250px;
   max-width: 370px;
   min-height: 44px;
   position: relative;
   overflow: hidden;
 }

 .submit-btn {
   background: var(--ink-black, #000000);
   border-radius: var(--components-button-radius, 1000px);
   padding: 0px var(--spacing-24px, 24px) 0px var(--spacing-24px, 24px);
   display: flex;
   flex-direction: row;
   gap: var(--spacing-12px, 12px);
   align-items: center;
   justify-content: center;
   flex-shrink: 0;
   height: 48px;
   position: relative;
   overflow: hidden;
   color: var(--components-button-primary-text, #ffffff);
   text-align: center;
   font-family: var(--text-body-font, "Roboto-SemiBold", sans-serif);
   font-size: 16px;
   line-height: 24px;
   font-weight: 600;
   margin-top: 41px;
 }

 /* .submit-btn:hover {
        background-color: #333;
      } */

 .close-icon {
   position: absolute;
   top: 20px;
   right: 35px;
   font-size: 20px;
   cursor: pointer;
   color: #333;
   font-weight: bold;
   border: none;
   background: none;
 }

 @media (max-width: 480px) {
   .modal {
     padding: 24px 16px;
     gap: 16px;
     margin:0 auto;
   }

   .submit-btn {
     width: 100%;
     text-align: center;
     justify-content: center;
   }

   .close-icon {
     font-size: 18px;
