/*
script.js
Updated 04/10/2024
*/

var boxesContainer = document.getElementById('boxes');
var btn = document.getElementById('btn');
if (btn) {
  btn.addEventListener('click', () => boxesContainer.classList.toggle('big'));
}
function createBoxes() {
  for (let i = 0; i < 4; i++) {
    for (let j = 0; j < 4; j++) {
      var box = document.createElement('div');
      box.classList.add('box');
      box.style.backgroundPosition = `${-j * 125}px ${-i * 125}px`;
      if (boxesContainer) {
        boxesContainer.appendChild(box);
      }
    }
  }
}

createBoxes();

function validateZipCode(input) {
  const zipCodeRegex = /^[0-9a-zA-Z]{3,11}$/;
  return zipCodeRegex.test(input);
}

function showPassword(inputId, iconId) {
  var x = document.getElementById(inputId);
  var i = document.getElementById(iconId);
  if (x.type === 'password') {
    x.type = 'text';
    i.src =
      'https://www.hyattvacationclub.com/files/live/sites/hvc-marketing/files/owners_consolidation/icons/password-show.png';
  } else {
    x.type = 'password';
    i.src =
      'https://www.hyattvacationclub.com/files/live/sites/hvc-marketing/files/owners_consolidation/icons/password-hide.png';
  }
}

/* Method to validate email address */
function validateEmail(email) {
  var emailRegex =
    /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
  return emailRegex.test(String(email).toLowerCase());
}

/* Method to validate phone number */
function validatePhoneNumber(phoneNumber) {
  // Check if the phone number is a number and has a length between 7 and 15 digits
  return /^\d{7,15}$/.test(phoneNumber);
}

/* 
  Method to validate password with below rules:
  1. Length should be between 8 and 16 characters
  2. At least 1 capital letter
  3. At least 1 lower case letter
  4. At least 1 number
  5. At least 1 special character
*/

var newPasswordInput = document.getElementById('new-password');
var confirmPasswordInput = document.getElementById('confirm-password');
var submitButton = document.getElementById('submit-button');
var passwordLengthError = document.getElementById('password-length-error');
var uppercaseError = document.getElementById('uppercase-error');
var lowercaseError = document.getElementById('lowercase-error');
var numberError = document.getElementById('number-error');
var specialCharError = document.getElementById('special-char-error');

if (submitButton) {
  submitButton.disabled = true;
}

// Function to check if password meets the criteria
function validatePassword(password) {
  var regex = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]).{8,16}$/;
  return regex.test(password);
}

// Function to check if new password and confirm password match
function checkPasswordMatch() {
  var newPassword = newPasswordInput.value;
  var confirmPassword = confirmPasswordInput.value;

  if (validatePassword(newPassword) && validatePassword(confirmPassword)) {
    if (newPassword === confirmPassword) {
      submitButton.disabled = false;
    } else {
      submitButton.disabled = true;
    }
  } else {
    submitButton.disabled = true;
  }
}

// Add event listeners to new password and confirm password inputs
if (newPasswordInput) {
  newPasswordInput.addEventListener('input', function () {
    var password = newPasswordInput.value;
    let errors = [];

    if (password.length < 8 || password.length > 16) {
      errors.push('Password should be between 8 and 16 characters');
    }

    if (!/[A-Z]/.test(password)) {
      errors.push('Password should include at least one uppercase letter');
    }

    if (!/[a-z]/.test(password)) {
      errors.push('Password should include at least one lowercase letter');
    }

    if (!/\d/.test(password)) {
      errors.push('Password should include at least one number');
    }

    if (!/[^A-Za-z0-9]/.test(password)) {
      errors.push('Password should include at least one special character');
    }

    // Display error messages individually on the DOM
    if (errors.includes('Password should be between 8 and 16 characters')) {
      passwordLengthError.classList.remove('valid');
      passwordLengthError.classList.add('invalid');
    } else {
      passwordLengthError.classList.remove('invalid');
      passwordLengthError.classList.add('valid');
    }

    if (errors.includes('Password should include at least one uppercase letter')) {
      uppercaseError.classList.remove('valid');
      uppercaseError.classList.add('invalid');
    } else {
      uppercaseError.classList.remove('invalid');
      uppercaseError.classList.add('valid');
    }

    if (errors.includes('Password should include at least one lowercase letter')) {
      lowercaseError.classList.remove('valid');
      lowercaseError.classList.add('invalid');
    } else {
      lowercaseError.classList.remove('invalid');
      lowercaseError.classList.add('valid');
    }

    if (errors.includes('Password should include at least one number')) {
      numberError.classList.remove('valid');
      numberError.classList.add('invalid');
    } else {
      numberError.classList.remove('invalid');
      numberError.classList.add('valid');
    }

    if (errors.includes('Password should include at least one special character')) {
      specialCharError.classList.remove('valid');
      specialCharError.classList.add('invalid');
    } else {
      specialCharError.classList.remove('invalid');
      specialCharError.classList.add('valid');
    }

    checkPasswordMatch();
  });
}

if (confirmPasswordInput) {
  confirmPasswordInput.addEventListener('input', function () {
    checkPasswordMatch();
  });
}

/* Login Page Validation Start */

var usernameInput = document.getElementById('login-username');
var usernameError = document.getElementById('login-username-error');
var passowrdInput = document.getElementById('login-password');
var passwordError = document.getElementById('login-password-error');
var loginBtn = document.getElementById('login');
if (loginBtn) {
  loginBtn.disabled = true;
}

function shouldEnableLogin() {
  if (loginBtn) {
    if (
      usernameError.textContent === '' &&
      usernameInput.value &&
      passwordError.textContent === '' &&
      passowrdInput.value
    ) {
      loginBtn.disabled = false;
    } else {
      loginBtn.disabled = true;
    }
  }
}

if (usernameInput) {
  usernameInput.addEventListener('input', function () {
    if (usernameInput.value.trim().length > 0) {
      if (usernameError) {
        usernameError.textContent = '';
      }
      usernameInput.parentElement.querySelector('input[type="text"]').classList.remove('login--custom-input-error');
      usernameInput.parentElement
