module.exports = a = async ({params}) => {
    console.log('params: ', params)
    try {
        // Generate a 6-digit random number (as a string)
        const pwRecoveryCode = Math.floor(100000 + Math.random() * 900000).toString()

        return { pwRecoveryCode }
    } catch (error) {
        console.error('Error generating password recovery code:', error)
        throw new Error('Failed to generate password recovery code')
    }
