const ownerLanguages = [
  { code: "en", name: "English" },
  { code: "es", name: "Espaol" },
  { code: "ja", name: "" },
];
const vcapLanguages = [
  { code: "en", name: "English" },
  { code: "ja", name: "" },
  { code: "zh", name: "" }
];

const defaultLanguages = [
  { code: "en", name: "English" }
];
const langList= getLanguagesByDomain(domain);
function getLanguagesByDomain(domain) {

  // For Marriott Vacation Club + Default
  if (domain.includes("marriottvacationclub.com") || domain === "") {
    return ownerLanguages;
  }

  // For VCAP
  if (domain.includes("vacationclubap.com")) {
    return vcapLanguages;
  }

  // If domain is unknown  default
  console.warn(`Unknown domain '${domain}'. Using default language list.`);
  return defaultLanguages;
}


const els = {
  userIcon: document.getElementById("userIcon"),
  desktopLanguageTrigger: document.getElementById("desktopLanguageTrigger"),
  desktopDropdownOptions: document.getElementById("desktopDropdownOptions"),
  languageButtonArrow: document.getElementById("desktopArrow"),
  hamburgerBtn: document.getElementById('hamburgerBtn'),
  closeBtn: document.getElementById('closeBtn'),
  welcomeCloseBtn: document.getElementById('welcomeCloseBtn'),
  loginToggleButton: document.getElementById('loginToggleButton'),
  mobileMenu: document.getElementById('mobileMenu')
};
const CLASSES = Object.freeze({
  hidden: "hidden",
  open: "open",
  invalid: "invalid",
  expanded: "expanded"
});

function bindClickOrTouch(el, handler) {
  if (!el) return;
  let lastTouchTimestamp = 0;
  const wrapper = (e) => {
    e.stopPropagation();
    if (e.type === "touchend") lastTouchTimestamp = Date.now();
    else if (e.type === "click" && (Date.now() - lastTouchTimestamp) < 500) {
      e.preventDefault();
      return;
    }
    handler(e);
  };
  el.addEventListener("touchend", wrapper, { passive: false });
  el.addEventListener("click", wrapper);
}
function setupMenus() {
    if (els.hamburgerBtn) bindClickOrTouch(els.hamburgerBtn, () => els.mobileMenu?.classList.add(CLASSES.open));
    if (els.closeBtn) bindClickOrTouch(els.closeBtn, () => els.mobileMenu?.classList.remove(CLASSES.open));
    if (els.welcomeCloseBtn) bindClickOrTouch(els.welcomeCloseBtn, () => els.welcomeModal.style.display = "none");
    if (els.loginToggleButton) bindClickOrTouch(els.loginToggleButton, () => {
        isLoggedIn = !isLoggedIn;
        updateUI();
    });
}
// Get saved language
const getLanguagePreference = () => {
  const match = document.cookie.match(/langPref=([^;]+)/);
  return match ? match[1] : "en";
};


function populateLanguages(langList) {
  if (!els.desktopDropdownOptions) return;

  els.desktopDropdownOptions.innerHTML = ""; // clear old items

  langList.forEach(lang => {
    const li = document.createElement("li");
    li.textContent = lang.name;
    li.dataset.langCode = lang.code;
    els.desktopDropdownOptions.appendChild(li);
  });
// }

  // Ensure it is actually an array
  if (!Array.isArray(langList)) {
    console.error("langList is not an array:", langList);
    return;
  }

  // Now safe to use forEach
  langList.forEach(lang => {
    console.log("Language:", lang);
  });
}


function setInitialLanguageButtonText() {
  const lang = getLanguagePreference();
  const langList = getLanguagesByDomain(domain);
  const selected =langList ?  langList?.find(l => l.code === lang) || langList[0] : null;

  const textEl = document.getElementById("desktopLanguageTriggerText");
  if (textEl) textEl.textContent = selected?.name;
}
function setInitialLanguageButtonText() {
  const savedLang = getLanguagePreference();

  // find the language in the list, else fallback to first option
  const selected = langList.find(l => l.code === savedLang) || langList[0];

  const textEl = document.getElementById("desktopLanguageTriggerText");
  if (textEl) textEl.textContent = selected.name;
}


function updateLanguageDisplay(langCode) {
  Array.from(els.desktopDropdownOptions.children).forEach(li => {
    li.style.backgroundColor = li.dataset.langCode === langCode ? "#f0f0f0" : "";
  });
}

els.desktopDropdownOptions.addEventListener("click", (e) => {
  const target = e.target.closest("li[data-lang-code]");
  if (!target) return;

  const selectedLang = target.dataset.langCode;

  // Save cookie
  document.cookie =
    `langPref=${selectedLang};path=/;max-age=2592000;SameSite=Lax`;

  updateLanguageDisplay(selectedLang);

  location.reload(); // apply language immediately
});

function setupDropdowns() {
  // Toggle user dropdown
  els.userIcon.addEventListener("click", (event) => {
    const isOpen = els.userIcon.classList.contains("open");

    // If already open, close it
    console.log('userIcon',event)
      const languageBtn = document.getElementById("languageButton");
 if (event.target.classList.contains("mobile-userIcon")) {
        console.log("Mobile user icon clicked");
        
       
    }

    // 2. Click on #languageButton OR its child elements
  
    if (languageBtn.contains(event.target)) {
        console.log("Language button (or child) clicked");
       
          els.userIcon.classList.toggle("open");
        els.desktopDropdownOptions.classList.toggle("open");
    }

    if (isOpen)  {
     
      console.log("Language button isOpen");
    } else {
   
      els.userIcon.classList.add("open");
    }
  });

  // For language arrow click
  els.languageButtonArrow.addEventListener("click", () => {
    const isOpen = els.desktopDropdownOptions.classList.contains("open");

    // If already open, close it
    if (isOpen) {
      closeAllDropdowns("language");
    } else {
    
      els.desktopDropdownOptions.classList.add("open");
    }
  });

  // For language trigger click
  els.desktopLanguageTrigger.addEventListener("click", () => {
    const isOpen = els.desktopDropdownOptions.classList.contains("open");

    
    if (isOpen) {
    els.desktopDropdownOptions.classList.toggle("open");
    } else {
   
      els.desktopDropdownOptions.classList.toggle("open");
    }
  });

  // Language selection
  els.desktopDropdownOptions.addEventListener("click", (e) => {
    const target = e.target.closest("li[data-lang-code]");
    if (!target) return;
    const selectedLang = target.dataset.langCode;
    document.cookie = `langPref=${selectedLang};path=/;max-age=2592000;SameSite=Lax`;
    location.reload();
  });

  // Click outside to close all dropdowns
  window.addEventListener("click", (e) => {
    const clickedInsideUser = els.userIcon.contains(e.target);
    const clickedInsideLanguage =
      els.desktopLanguageTrigger.contains(e.target) ||
      els.languageButtonArrow.contains(e.target) ||
      els.desktopDropdownOptions.contains(e.target);

    if (!clickedInsideUser && !clickedInsideLanguage) {
      closeAllDropdowns();
    }
  });

  // --- Helper: close specific dropdowns ---
  function closeOtherDropdowns(openOne) {
    if (openOne !== "user") els.userIcon.classList.remove("open");
    if (openOne !== "language") {
      els.desktopLanguageTrigger.classList.remove("open");
      els.desktopDropdownOptions.classList.remove("open");
    }
  }

  // --- Helper: close all dropdowns ---
  function closeAllDropdowns() {
    els.userIcon.classList.remove("open");
    els.desktopLanguageTrigger.classList.remove("open");
    els.desktopDropdownOptions.classList.remove("open");
  }
}
function setupPasswordToggles() {
    // Select all groups with password + eye
    const groups = document.querySelectorAll(".input-group");

    groups.forEach(group => {
        const input = group.querySelector("input[type='password'], input[type='text']");
        const eyeIcon = group.querySelector(".eyeballicon");

        if (input && eyeIcon) {
            eyeIcon.addEventListener("click", () => {
                const isPassword = input.type === "password";
                input.type = isPassword ? "text" : "password";

                // Swap icon depending on state
                eyeIcon.src = isPassword
                    ? "https://content.marriottvacationclub.com/icon/visibility.png" //  open eye
                    : "https://content.marriottvacationclub.com/icon/viewPassword.png";   //  crossed eye
            })
        }
    });
}


