const form = document.getElementById("user-info-form");

// Trim while typing (remove leading spaces)
form.addEventListener("input", function (event) {
  if (event.target.tagName === "INPUT") {
    console.log(event.target.tagName.value)
    event.target.value = event.target.value.trim();
    console.log(event.target.value)
  }
});

// Full trim when user leaves the field
form.addEventListener("change", function (event) {
  if (event.target.tagName === "INPUT") {
    event.target.value = event.target.value.trim();
  }
});

// Extra safety: trim all fields on submit
form.addEventListener("submit", function () {
  const inputs = form.querySelectorAll("input");
  inputs.forEach(input => {
    input.value = input.value.trim();
    console.log(input.value)
  });
});



// const domain = "{{local.rx44z5nqd9.payload.output.domain}}";
// const moxieKey = getMoxieKey(domain);


/*Validation On Input */
// const form = document.getElementById("user-info-form");
let allowSubmit = false;
const fields = [
  { id: "first-name", errorId: "firstNameError", label: "First Name" },
  { id: "last-name", errorId: "lastNameError", label: "Last Name" },
  { id: "owner-id", errorId: "customerIdError", label: "Customer ID#" },
  { id: "email", errorId: "emailError", label: "Email" }
];

const touchedFields = {};

// Attach blur and input events
fields.forEach(({ id, errorId, label }) => {
  const input = document.getElementById(id);
  const error = document.getElementById(errorId);

  input.addEventListener("blur", () => {
    touchedFields[id] = true;
    checkInput(input, error, label);
  });

  input.addEventListener("input", () => {
    if (touchedFields[id]) {
      console.log("Field touched")
      checkInput(input, error, label);
    }
  });
});

// Handle form submission
const continueBtn = document.getElementById('continue-button');

continueBtn.addEventListener("click", function (e) {
  e.preventDefault();
  console.log("In button click")
  let isValid = true;

  fields.forEach(({ id, errorId, label }) => {
    const input = document.getElementById(id);
    const error = document.getElementById(errorId);
    touchedFields[id] = true;

    const fieldIsValid = checkInput(input, error, label);
    if (!fieldIsValid) {
      isValid = false;
      console.log("Please Enter details");
      formErrorId.style.display = "flex";
      formErrorMessage.textContent = "Please enter details."

    }
  });

  if (isValid) {
    console.log(" Form is valid. Submitting...");
    formErrorId.style.display = "none";
    allowSubmit = true;

  }
  else {
    console.warn(" Form is invalid. Please correct the errors.");
    e.preventDefault();// stopping form submit
    e.stopPropagation();
    allowSubmit = false;
    // Focus first invalid field
    const firstInvalid = fields.find(({ id }) => {
      return !validateInput(document.getElementById(id));
    });
    if (firstInvalid) {
      document.getElementById(firstInvalid.id).focus();
    }
  }
});


// Validates the input and shows/hides error accordingly
function checkInput(input, errorElement, label) {
  const value = input.value.trim();

  if (!validateInput(input)) {
    let message = `Enter your ${label}`;

    if (input.type === "email" && value !== "") {
      message = `Please enter a valid email address`;
    }

    showError(input, errorElement, message);
    return false;
  } else {
    hideError(input, errorElement);
    return true;
  }
}

// Returns true if input is valid
function validateInput(input) {
  const value = input.value.trim();

  if (value === "") {
    console.log("NO value")
    return false;
  }

  if (input.type === "email") {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(value);
  }

  return true;
}

// Displays the error message and red border
function showError(input, errorElement, message) {
  input.style.border = "1px solid red";
  errorElement.style.color = "red";
  errorElement.style.fontSize = "12px";
  errorElement.textContent = message;
  console.log("Red border should show")
}

// Hides the error and resets border
function hideError(input, errorElement) {
  input.style.border = "";
  errorElement.textContent = "";
  formErrorId.style.display = "none";
}


/*Validation end*/



