module.exports = a = async ({params}) => {
    console.log('params: ', params)

    try {
        if (!params.input) {
             return {
                error: {
                    code: 500,
                    message:  "Missing input"
                }
            };
        }

        const parsedInput = typeof params.input === 'string' ? JSON.parse(params.input) : params.input;

        if (!parsedInput.rawResponse) {
           return {
                error: {
                    code: 500,
                    message:  "Missing rawResponse in input"
                }
            };
        }

        const rawResponse = JSON.parse(parsedInput.rawResponse);

        if (!rawResponse.body) {
            return {
                error: {
                    code: 500,
                    message:  "Missing body in rawResponse"
                }
            };
        }

        const body = JSON.parse(rawResponse.body);
         if(body.success) {
            return {
                success: body.success,
                authenticationComplete: body.authenticationComplete,
                message: body.message,
                accessToken: body.accessToken,
                idToken: body.idToken,
                refreshToken: body.refreshToken,
                tokenType: body.tokenType,
                expiresIn: body.expiresIn,
                challengeName: body.challengeName,
                challengeParameters: body.challengeParameters,
                session: body.session
            }
        } else {
            return {
                error: {
                    code: rawResponse.statusCode || 500,
                    message: body.message || "Unknown error occurred",
                    errorCode: body.errorCode
                }
            };
        }



    } catch (err) {
        console.error('Error parsing input: ', err)
         return {
                error: {
                    code: 500,
                    message:  "Unknown error occurred"
                }
            };
    }
}

customCSS.value:
/* -------- Base Modal -------- */
.modal-container,
.modal-container * {
  box-sizing: border-box;
}

.modal-container {
  background: #ffffff;
  border-radius: 10px;
  padding: 48px;
  display: flex;
  flex-direction: column;
  gap: 24px;
  align-items: flex-end;
  justify-content: flex-start;
  min-width: 352px;
  max-width: 760px;
  position: relative;
  box-shadow: 0px 3px 5px 0px rgba(0, 0, 0, 0.3);
  overflow: hidden;
  margin: auto;
}

/* -------- Text Section -------- */
.modal-text-group {
  display: flex;
  flex-direction: column;
  gap: 12px;
  align-items: flex-start;
  justify-content: flex-start;
  align-self: stretch;
}

.modal-title {
  color: #000;
  text-align: left;
  font-family: "Roboto-SemiBold", sans-serif;
  font-size: 20px;
  line-height: 30px;
  font-weight: 600;
  display: flex;
  align-items: flex-end;
}

.modal-description {
  color: #000;
  text-align: left;
  font-family: "Roboto-Regular", sans-serif;
  font-size: 16px;
  line-height: 24px;
  font-weight: 400;
}

.modal-description span {
  font-family: "Roboto-Regular", sans-serif;
}

.modal-description .highlight {
  font-family: "Roboto-Bold", sans-serif;
  font-weight: 700;
  color: #000;
  cursor: pointer;
}

/* -------- Buttons -------- */
.modal-buttons {
  display: flex;
  flex-direction: row;
  gap: 24px;
  align-items: center;
  justify-content: flex-start;
  align-self: stretch;
}

.modal-button {
  background: #000;
  border-radius: 1000px;
  padding: 0 24px;
  height: 48px;
  cursor: pointer;
  border: none;
  color: #fff;
  font-family: "Roboto-SemiBold", sans-serif;
  font-size: 16px;
  line-height: 24px;
  font-weight: 600;
  display: flex;
  align-items: center;
  justify-content: center;
}

.modal-button:hover {
  background: #222;
}

/* -------- Responsive -------- */
/* Mobile (<600px) */
@media (max-width: 599px) {
  .modal-container {
    padding: 24px;
    min-width: 100%;
    border-radius: 0;
    gap: 16px;
  }

  .modal-title {
    font-size: 18px;
    line-height: 26px;
  }

  .modal-description {
    font-size: 14px;
    line-height: 20px;
  }

  .modal-button {
    width: 100%;
  }
}

/* Tablet (600px1023px) */
@media (min-width: 600px) and (max-width: 1023px) {
  .modal-container {
    padding: 32px;
    max-width: 600px;
  }

  .modal-title {
    font-size: 19px;
  }

