        flex-direction: column;
        align-items: flex-start;
        gap: 8px;
    }

    #login {
        width: 315px;
    }
}

.welcome-modal {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    height: 100vh;
    width: 100vw;
    background: rgba(0, 0, 0, 0.6);
    z-index: 9999;
    justify-content: center;
    align-items: center;
}

.modal-content {
    background: white;
    padding: 2rem;
    border-radius: 10px;
    max-width: 750px;
    width: 90%;
    position: relative;
}

.modal-title {
    font-size: 1.5rem;
    font-weight: bold;
    margin-bottom: 1rem;
}

.modal-subtitle {
    font-size: 1rem;
    line-height: 1.5;
    max-width: 100%;
    margin: 0 auto;
}

.welcome-modal button {
    margin-top: 20px;
