// ====================================
//                             Constants & Config
// ========================================================================


const MOXIE_SCRIPT_ID = 'moxie-translation-script';
const domain = "{{global.variables.domain}}";


console.log("domain  is:", domain);

const moxieKey = getMoxieKey(domain);
console.log("moxiekey is ", moxieKey);
console.log("domain is ", domain);

const IDS = Object.freeze({
    username: "login-username",
    usernameError: "login-username-error",
    password: "login-password",
    passwordError: "login-password-error",
    rememberMe: "rememberMe",
    loginBtn: "login",
    loginForm: "frmLogin",
    passwordIcon: "password-icon",
    hamburgerBtn: "hamburgerBtn",
    mobileMenu: "mobileMenu",
    closeBtn: "closeBtn",
    menuLoggedIn: "menuLoggedIn",
    menuLoggedOut: "menuLoggedOut",
    loginToggleButton: "loginButton",
    welcomeCloseBtn: "welcomeCloseBtn",
    welcomeModal: "welcomeModal",
    desktopLanguageDropdown: "dropdownOptions",
    loginPageLanguageDropdown: "languageDropdown",
    loginPageLanguageButton: "languageButton",
    loginPageLanguageButtonText: "languageButtonText",
    userIcon: "userIcon",
    mobileLanguageTrigger: "mobileLanguageTrigger",
    logoText: "logoText",
    mobileLanguageToggle: "mobileLanguageToggle",
    mobileMenuLanguageList: "mobileMenuLanguageList"
});

const CLASSES = Object.freeze({
    hidden: "hidden",
    open: "open",
    invalid: "invalid",
    expanded: "expanded"
});

const ownerLanguages = [
    { code: "en", name: "English" },
    { code: "es", name: "Espaol" },
    { code: "ja", name: "" }
];
const vcapLanguages = [
    { code: "en", name: "English" },
    { code: "ja", name: "" },
    { code: 'zh', name: "  " }
];

const defaultLanguages = [
    { code: "en", name: "English" }
];

// Select correct languages array based on moxieKey
function getLanguagesForMoxieKey(moxieKey) {
    switch (moxieKey) {
        case "{{global.company.variables.moxieKeyMVC}}":
            return ownerLanguages;
        case "{{global.company.variables.moxieKeyVCAP}}":
            return vcapLanguages;
        default:
            return defaultLanguages;
    }
}
function setOwnerSiteUrl() {
      const domain = "{{global.variables.domain}}";

    // Mapping domain  URL variable
    const urlMap = {
        "grandresidenceclub.com": "{{global.company.variables.grcOwnerSiteUrl}}",
        "myritzcarltonclub.com": "{{global.company.variables.rcdcOwnerSiteUrl}}",
        "vacationclubap.com": "{{global.company.variables.vcapOwnerSiteUrl}}",
        'marriottvacationclub.com': "{{global.company.variables.mvcOwnerSiteUrl}}"
    };

    // Find correct URL from map
    const finalUrl = urlMap[domain] || "#";  // fallback
    console.log(finalUrl);

    // Assign to <a> tag
    document.getElementById("ownerSiteLink").href = finalUrl;
}
console.log('domain')
setOwnerSiteUrl();
// ========================================================================
//                           Global State
// ========================================================================

const els = {};
let hasUsernameBeenTouched = false;
let hasPasswordBeenTouched = false;
let isLoggedIn = false;
let isAppInitialized = false;

// ========================================================================
//                      MoxieJS Language Management
// ========================================================================

function getLanguagePreference() {
    const langCookie = document.cookie.split('; ').find(row => row.startsWith('langPref='));
    return langCookie ? langCookie.split('=')[1] : 'en';
}

function applyMoxieTranslations() {

    const lang = getLanguagePreference();
    console.log(`DEV MODE: Moxie script injection skipped. It would have translated the page to '${lang}'.`);

    const existingScript = document.getElementById(MOXIE_SCRIPT_ID);
    if (existingScript) existingScript.remove();

    const moxieScript = document.createElement("script");
    moxieScript.id = MOXIE_SCRIPT_ID;
    moxieScript.src = "https://www.onelink-edge.com/moxie.min.js";
    moxieScript.type = "text/javascript";
    moxieScript.setAttribute("data-oljs", moxieKey);
    moxieScript.onerror = () => console.error("CRITICAL: Moxie script failed to load.");
    document.head.appendChild(moxieScript);
}

// ========================================================================
//               Application Logic & UI Setup
// ========================================================================

function bindClickOrTouch(el, handler) {
    if (!el) return;
    let lastTouchTimestamp = 0;
    const wrapper = (e) => {
        e.stopPropagation();
        if (e.type === "touchend") lastTouchTimestamp = Date.now();
        else if (e.type === "click" && (Date.now() - lastTouchTimestamp) < 500) {
            e.preventDefault();
            return;
        }
        handler(e);
    };
    el.addEventListener("touchend", wrapper, { passive: false });
    el.addEventListener("click", wrapper);
}

function createLanguageItem(lang) {
    const li = document.createElement("li");
    li.textContent = lang.name;
    li.dataset.langCode = lang.code;
    return li;
}


function populateLanguages() {
    if (!moxieKey) {
        console.warn("No Moxie Key found. Hiding language dropdowns.");
        [els.desktopLanguageDropdown, els.loginPageLanguageDropdown, els.mobileMenuLanguageList].forEach(list => {
            if (list) list.style.display = "none";   // 
        });
        if (els.loginPageLanguageButton) {
            els.loginPageLanguageButton.style.display = "none"; // 
        }
        if (els.mobileLanguageTrigger) {
            els.mobileLanguageTrigger.style.display = "none"; // 
        }
        return; // stop here
    }

    const currentLanguages = getLanguagesForMoxieKey(moxieKey);
    const lists = [els.desktopLanguageDropdown, els.loginPageLanguageDropdown, els.mobileMenuLanguageList];
    lists.forEach(list => { if (list) list.innerHTML = ""; });
    currentLanguages.forEach(lang => {
        lists.forEach(list => { if (list) list.appendChild(createLanguageItem(lang)); });
    });
}

function updateLanguageDisplay(langCode) {
    const currentLanguages = getLanguagesForMoxieKey(moxieKey);
    [els.desktopLanguageDropdown, els.loginPageLanguageDropdown, els.mobileMenuLanguageList].forEach(ul => {
        if (!ul) return;
        Array.from(ul.children).forEach(li => {
            li.style.backgroundColor = li.dataset.langCode === langCode ? "#f0f0f0" : "";
        });
    });
    const langName = (currentLanguages.find(l => l.code === langCode) || currentLanguages[0]).name;
    if (els.loginPageLanguageButtonText) {
        els.loginPageLanguageButtonText.textContent = langName;
    }
}

