        });
    }
}

function setupMenus() {
    if (els.hamburgerBtn) bindClickOrTouch(els.hamburgerBtn, () => els.mobileMenu?.classList.add(CLASSES.open));
    if (els.closeBtn) bindClickOrTouch(els.closeBtn, () => els.mobileMenu?.classList.remove(CLASSES.open));
    if (els.welcomeCloseBtn) bindClickOrTouch(els.welcomeCloseBtn, () => els.welcomeModal.style.display = "none");
    if (els.loginToggleButton) bindClickOrTouch(els.loginToggleButton, () => {
        isLoggedIn = !isLoggedIn;
        updateUI();
    });
}

function setupValidation() {
    const applyError = (el, errorEl, msg) => {
        if (el) el.classList.toggle(CLASSES.invalid, !!msg);
        if (errorEl) errorEl.textContent = msg || "";
    };
    const validate = () => {
        if (hasUsernameBeenTouched) applyError(els.username, els.usernameError, els.username.value ? "" : "Enter your username.");
        if (hasPasswordBeenTouched) applyError(els.password, els.passwordError, els.password.value ? "" : "Enter your password.");
    };
    if (els.username) els.username.addEventListener('blur', () => { hasUsernameBeenTouched = true; validate(); });
    if (els.password) els.password.addEventListener('blur', () => { hasPasswordBeenTouched = true; validate(); });
}

function updateUI() {
    if (els.menuLoggedIn) els.menuLoggedIn.style.display = isLoggedIn ? "block" : "none";
    if (els.menuLoggedOut) els.menuLoggedOut.style.display = isLoggedIn ? "none" : "block";
    const userFirstNameEl = els.mobileMenu?.querySelector("div > p > strong");
    if (userFirstNameEl) userFirstNameEl.closest('div').style.display = isLoggedIn ? "block" : "none";
}

function setupAllDropdowns() {
    bindClickOrTouch(els.userIcon, () => els.userIcon?.classList.toggle(CLASSES.open));
    bindClickOrTouch(els.mobileLanguageTrigger, () => els.mobileLanguageTrigger?.classList.toggle(CLASSES.open));
    bindClickOrTouch(els.loginPageLanguageButton, () => els.loginPageLanguageDropdown?.classList.toggle(CLASSES.hidden));
    bindClickOrTouch(els.mobileLanguageToggle, () => {
        els.mobileLanguageToggle?.classList.toggle(CLASSES.expanded);
        els.mobileMenuLanguageList?.classList.toggle(CLASSES.hidden);
    });

    const handleLanguageClick = (e) => {
        const targetLi = e.target.closest('li[data-lang-code]');
        if (!targetLi) return;
        const langCode = targetLi.dataset.langCode;
        const currentLang = getLanguagePreference();

        updateLanguageDisplay(langCode);

        if (currentLang !== langCode) {
            console.log(`Language changed to '${langCode}'. Setting cookie.`);
            document.cookie = `selectedLanguage=${langCode}`;
            // applyMoxieTranslations();
            location.reload();
        }

        els.userIcon?.classList.remove(CLASSES.open);
        els.loginPageLanguageDropdown?.classList.add(CLASSES.hidden);
        els.mobileMenu?.classList.remove(CLASSES.open);
    };

    [els.desktopLanguageDropdown, els.loginPageLanguageDropdown, els.mobileMenuLanguageList].forEach(list => {
        if (list) bindClickOrTouch(list, handleLanguageClick);
    });

    window.addEventListener('click', () => {
        if (els.userIcon) els.userIcon.classList.remove(CLASSES.open);
        if (els.loginPageLanguageDropdown) els.loginPageLanguageDropdown.classList.add(CLASSES.hidden);
    });
}

// ========================================================================
//                       Initialization Function
// ========================================================================

function initializeApp() {
    if (isAppInitialized) return true;
    for (const key in IDS) els[key] = document.getElementById(IDS[key]);
    if (!els.userIcon) return false;

    console.log("Key element #userIcon found. Initializing application...");
    isAppInitialized = true;
    setOwnerSiteUrl();
    
    setupPasswordToggle();
    setupPasswordIconVisibility();
    setupMenus();
    setupValidation();
    const moxieKey = getMoxieKey(domain);
    populateLanguages();
    setupAllDropdowns();
    updateUI();

    const initialLangCode = getLanguagePreference();
    if (document.cookie.includes('langPref=')) {
        console.log(`Found language preference cookie: langPref=${initialLangCode}`);
    } else {
        console.log("No language preference cookie found. Defaulting to 'en'.");
    }

    updateLanguageDisplay(initialLangCode);
    applyMoxieTranslations();

    console.log("Application setup complete.");
    return true;
}

// ========================================================================
//                     Universal Entry Point
// ========================================================================

const initInterval = setInterval(() => {
    if (initializeApp()) {
        clearInterval(initInterval);
        console.log("Initialization successful. Polling stopped.");
    }
}, 100);

setTimeout(() => {
    clearInterval(initInterval);
}, 10000);



