// ------------------ Config ------------------


// --- NEW: Unique IDs for our dynamic elements ---
const BACKGROUND_IFRAME_ID = 'dynamic-background-iframe';
const BACKGROUND_OVERLAY_ID = 'dynamic-background-overlay';

function getMoxieKey(domain) {
    switch (domain) {
        case "marriottvacationclub.com":
            return "{{global.company.variables.moxieKeyMVC}}";
        case "vacationclubap.com":
            return "{{global.company.variables.moxieKeyVCAP}}";
        default:
            // console.warn(`Unknown domain '${domain}'. Falling back to MVC key.`);
            console.warn(`Unknown domain '${domain}'. Moxie key left empty.`);
            return "";

    }
}
const domain = "{{global.variables.domain}}";
console.log("Domain  is:", domain);
const MOXIE_SCRIPT_ID = 'moxie-translation-script';
const moxieKey = getMoxieKey(domain);
console.log("Moxie Key is:", moxieKey);



// Track whether background is currently active
let backgroundVisible = false;

/**
 * Creates and injects a dimmed, full-screen iframe background.
 * Checks if elements already exist to avoid duplicates.
 * @param {string} url - The URL for the background website.
 */
function insertBackgroundSiteDimmed(url) {
    if (document.getElementById(BACKGROUND_IFRAME_ID)) return;

    // Create the dimming overlay
    const overlay = document.createElement('div');
    overlay.id = BACKGROUND_OVERLAY_ID;
    overlay.style.cssText = `
      position:fixed; top:0; left:0; width:100vw; height:100vh;
      background:rgba(0,0,0,0.8); z-index:-1;
    `;

    // Create the iframe
    const iframe = document.createElement('iframe');
    iframe.id = BACKGROUND_IFRAME_ID;
    iframe.src = url;
    iframe.style.cssText = `
      position:fixed; top:0; left:0; width:100vw; height:100vh;
      border:none; z-index:-2;
    `;
    iframe.setAttribute('sandbox', 'allow-scripts allow-same-origin');

    // Add them to the page
    document.body.insertBefore(overlay, document.body.firstChild);
    document.body.insertBefore(iframe, document.body.firstChild);

    console.log("Dimmed background and iframe have been injected.");
}


// --- 1. Main Logic to Control the Background ---
const manageBackgroundVisibility = () => {
    const modal = document.getElementById('accoundNotFoundModal');

    if (modal && !backgroundVisible) {


        insertBackgroundSiteDimmed("{{global.company.variables.backGroundImageGuestConnect}}");
        backgroundVisible = true;

    } else if (!modal && backgroundVisible) {
        removeBackgroundSiteDimmed();
        backgroundVisible = false;
    }
};

// --- 2. Watch for Page Changes in the SPA ---
const observer = new MutationObserver(manageBackgroundVisibility);
observer.observe(document.body, { childList: true, subtree: true });

// --- 3. Initial Setup ---
manageBackgroundVisibility();


// function applyMoxieTranslations() {

//     const existingScript = document.getElementById(MOXIE_SCRIPT_ID);
//     if (existingScript) existingScript.remove();
//     const moxieScript = document.createElement("script");
//     moxieScript.id = MOXIE_SCRIPT_ID;
//     moxieScript.src = "https://www.onelink-edge.com/moxie.min.js";
//     moxieScript.type = "text/javascript";
//     moxieScript.setAttribute("data-oljs", moxieKey);
//     moxieScript.onerror = () => console.error("CRITICAL: Moxie script failed to load.");
//     document.head.appendChild(moxieScript);
// }

// applyMoxieTranslations();



function removeBackgroundSiteDimmed() {
    const iframe = document.getElementById(BACKGROUND_IFRAME_ID);
    const overlay = document.getElementById(BACKGROUND_OVERLAY_ID);

    if (iframe) iframe.remove();
    if (overlay) overlay.remove();

    console.log("Dimmed background and iframe have been removed.");
}

// On page unload
window.addEventListener('beforeunload', () => {
  removeBackgroundSiteDimmed();
});

// Get anchor element
const contactLink = document.querySelector(".cos-link");



// Get language cookie
function getCookie(name) {
  const cookies = document.cookie.split("; ");

  for (let c of cookies) {
    const [key, value] = c.split("=");
    if (key === name) {      // exact match
      return value;
    }
  }
  return null;
}

// const lang = getCookie("langpref");

const langPref = getCookie("langPref");  // default if missing

// Apply href based on domain
if (domain.includes("marriottvacationclub") || domain.includes("grandresidenceclub")) {
    // MVC & GRC
    contactLink.href = "{{global.variables.contactUsLink}}";
}

else if (domain.includes("vacationclubap")) {
    // VCAP
    contactLink.href = "{{global.company.variables.vcapLoggedInContactUs}}" + langPref;
}

else if (domain.includes("ritzcarltonclub")) {
    // RCDC
    contactLink.href = "{{global.company.variables.rcdcLoggedInContactUs}}";
}

