        min-width: 352px;
        max-width: 760px;
        width: 760px;
        position: relative;
        box-shadow: 0px 3px 5px rgba(0, 0, 0, 0.3);
    }

    .modal-header {
        display: flex;
        flex-direction: column;
        gap: 12px;
        align-items: flex-start;
        width: 100%;
    }

    .modal-title {
        font-family: "Roboto", sans-serif;
        font-size: 20px;
        font-weight: 600;
        color: #000;
    }

    .modal-message {
        font-family: "Roboto", sans-serif;
        font-size: 16px;
        font-weight: 400;
        line-height: 24px;
        color: #000;
    }

    .modal-actions {
        display: flex;
        flex-direction: row;
        gap: 12px;
        align-items: center;
        width: 100%;
        margin-top: 24px;
    }

    .btn-primary {
        background: #000;
        color: #fff;
        border-radius: 1000px;
        padding: 12px 24px;
        font-size: 16px;
        font-weight: 600;
        cursor: pointer;
        border: none;
    }

    .modal-close {
        position: absolute;
        top: 24px;
        right: 24px;
        font-size: 20px;
        font-weight: bold;
        color: #000;
        cursor: pointer;
        background-color: white;
        border: none;
    }

    /* Base (desktop) styles already in your code */

    /* Tablet (max-width: 1024px) */
    @media (max-width: 1024px) {
        .modal {
            min-width: 300px;
            max-width: 90%;
            /* shrink to fit screen */
            background: #ffffff;
            border-radius: 10px;
            padding: 48px;
            box-shadow: 0px 3px 5px rgba(0, 0, 0, 0.3);
        }

        .modal-title {
            font-size: 18px;
        }

        .modal-message {
            font-size: 15px;
            line-height: 22px;
        }

        .btn-primary {
            font-size: 15px;
            padding: 10px 20px;
        }
    }

    /* Mobile (max-width: 600px) */
    @media (max-width: 600px) {
        .parent {
            padding: 16px;
            /* add breathing room */
        }

        .modal {
            width: 100%;
            min-width: auto;
            max-width: 100%;
            height: auto;
            background: #ffffff;
            border-radius: 10px;
            padding: 48px;
            box-shadow: 0px 3px 5px rgba(0, 0, 0, 0.3);
        }

        .modal-title {
            font-size: 16px;
        }

        .modal-message {
            font-size: 14px;
            line-height: 20px;
        }

        .modal-actions {
            justify-content: center;
        }

        .btn-primary {
            width: 100%;
            /* full-width button */
            padding: 12px;
            font-size: 16px;
        }

        .modal-close {
            top: 16px;
            right: 16px;
        }
    }

