module.exports = a = async ({ params }) => {
    try {
        // -----------------------------
        // Helper functions
        // -----------------------------
        function parseBlackoutDays(value) {
            if (Array.isArray(value)) return value;
            if (typeof value === 'string') {
                try {
                    const parsed = JSON.parse(value);
                    if (Array.isArray(parsed)) return parsed;
                } catch (_) {}
                return value.split(',').map(v => v.trim()).filter(Boolean);
            }
            throw new Error('Invalid blackoutDays format.');
        }

        function parseCsvUserList(csv) {
            if (!csv || typeof csv !== 'string') return [];
            const trimmed = csv.trim();
            if (!trimmed) return [];
            if (!trimmed.includes('\n') && trimmed.includes(',')) {
                return trimmed.split(',').map(v => v.trim().toLowerCase()).filter(Boolean);
            }
            const lines = trimmed.split(/\r?\n/).map(l => l.trim()).filter(Boolean);
            const header = lines[0];
            const isHeader = /user|email|username/i.test(header);
            const users = (isHeader ? lines.slice(1) : lines)
                .map(l => l.split(',').map(v => v.trim().toLowerCase()))
                .flat()
                .filter(Boolean);
            return Array.from(new Set(users));
        }

        function normalizeDayName(name) {
            return name?.trim().toLowerCase();
        }

        function toLocalParts(dateObj, timeZone = 'America/New_York') {
            const fmt = new Intl.DateTimeFormat('en-US', {
                timeZone,
                year: 'numeric', month: '2-digit', day: '2-digit',
                hour: '2-digit', minute: '2-digit', hour12: false,
                weekday: 'long'
            });
            const parts = fmt.formatToParts(dateObj);
            const map = {};
            for (const p of parts) {
                if (p.type !== 'literal') map[p.type] = p.value;
            }
            return {
                year: Number(map.year),
                month: Number(map.month),
                day: Number(map.day),
                hour: Number(map.hour),
                minute: Number(map.minute),
                weekdayName: map.weekday
            };
        }

        function parseHHMM(hhmm) {
            const m = hhmm.trim().match(/^(\d{1,2}):(\d{2})$/);
            if (!m) throw new Error(`Invalid time string "${hhmm}". Use "HH:MM" format.`);
            const hh = Number(m[1]);
            const mm = Number(m[2]);
            if (hh < 0 || hh > 23 || mm < 0 || mm > 59) throw new Error(`Invalid time ${hhmm}`);
            return { hh, mm };
        }

        function dateYMDtoNumber(y, m, d) {
            return y * 10000 + m * 100 + d;
        }

        function inDateRange(localYMDNumber, startYMDStr, endYMDStr) {
            if (!startYMDStr || !endYMDStr) return false;
            const s = startYMDStr.trim().split('-').map(Number);
            const e = endYMDStr.trim().split('-').map(Number);
            if (s.length !== 3 || e.length !== 3) throw new Error('Invalid pilot date format.');
            const startNum = dateYMDtoNumber(s[0], s[1], s[2]);
            const endNum = dateYMDtoNumber(e[0], e[1], e[2]);
            return localYMDNumber >= startNum && localYMDNumber <= endNum;
        }

        // -----------------------------
        // Extract and validate inputs
        // -----------------------------
        const {
            username,
            blackoutDays,
            blackoutStartTime,
            blackoutEndTime,
            pilot1Csv,
            pilot2Csv,
            pilot1StartDate,
            pilot1EndDate,
            pilot2StartDate,
            pilot2EndDate,
            rolloutPercentage,
            cutoffDate,
            currentDateTime,
            debug
        } = params || {};

        const debugMode = (String(debug).toLowerCase() === 'true' || debug === true);

        if (!username) throw new Error('Missing required param: username');
        if (!blackoutDays) throw new Error('Missing required param: blackoutDays');
        if (!blackoutStartTime || !blackoutEndTime) throw new Error('Missing blackout time parameters.');
        if (!cutoffDate) throw new Error('Missing required param: cutoffDate');

        const rolloutPercentNum = Number(rolloutPercentage);
        if (isNaN(rolloutPercentNum) || rolloutPercentNum < 0 || rolloutPercentNum > 100)
            throw new Error('rolloutPercentage must be between 0 and 100.');

        const blackoutDayList = parseBlackoutDays(blackoutDays);

        const now = currentDateTime ? new Date(currentDateTime) : new Date();
        if (Number.isNaN(now.getTime())) throw new Error('Invalid currentDateTime.');

        const local = toLocalParts(now, 'America/New_York');
        const localYMDNumber = dateYMDtoNumber(local.year, local.month, local.day);
        const localTimeMinutes = local.hour * 60 + local.minute;
        const weekdayLower = local.weekdayName.toLowerCase();

        // -----------------------------
        // Debug info
        // -----------------------------
        const debugInfo = {
            timestamp: now.toISOString(),
            localTime: `${local.year}-${String(local.month).padStart(2, '0')}-${String(local.day).padStart(2, '0')} ${local.hour}:${String(local.minute).padStart(2, '0')} ${local.weekdayName}`,
            blackoutDaysParsed: blackoutDayList,
            evaluatedPath: '',
            rollValue: null
        };

        // -----------------------------
        // Cutoff check
        // -----------------------------
        const cutoffNum = dateYMDtoNumber(...cutoffDate.split('-').map(Number));
        const cutoffReached = localYMDNumber >= cutoffNum;

        if (cutoffReached) {
            debugInfo.evaluatedPath = 'cutoff';
            return {
                mfaDecision: {
                    success: true,
                    reason: `Cutoff date reached (${cutoffDate})  allow MFA for everyone.`,
                    rolloutPercentage: rolloutPercentNum,
                    pilotGroup: null,
                    pendingPilotGroup: null,
                    cutoffReached: true,
                    ...(debugMode ? { debugInfo } : {})
                }
            };
        }

        // -----------------------------
        // Blackout check
        // -----------------------------
        const blackoutSet = new Set((blackoutDayList || []).map(normalizeDayName));
        const start = parseHHMM(blackoutStartTime);
        const end = parseHHMM(blackoutEndTime);
        const startMinutes = start.hh * 60 + start.mm;
        const endMinutes = end.hh * 60 + end.mm;
        let inWindow = false;

        if (blackoutSet.has(weekdayLower)) {
            if (startMinutes <= endMinutes) {
                inWindow = localTimeMinutes >= startMinutes && localTimeMinutes < endMinutes;
            } else {
                inWindow = localTimeMinutes >= startMinutes || localTimeMinutes < endMinutes;
            }
        }

        if (inWindow) {
            debugInfo.evaluatedPath = 'blackout';
            throw new Error(JSON.stringify({
                errorType: 'BLACKOUT',
                reason: `Daily blackout (${blackoutStartTime}-${blackoutEndTime} ET) on ${local.weekdayName}.`,
                rolloutPercentage: rolloutPercentNum,
                pilotGroup: null,
                pendingPilotGroup: null,
                cutoffReached: false,
                ...(debugMode ? { debugInfo } : {})
            }));
        }

        // -----------------------------
        // Pilot group checks
        // -----------------------------
        const pilot1Users = parseCsvUserList(pilot1Csv || '');
        const pilot2Users = parseCsvUserList(pilot2Csv || '');
        const usernameLower = username.trim().toLowerCase();
        const pilot1Match = pilot1Users.includes(usernameLower);
        const pilot2Match = pilot2Users.includes(usernameLower);
        const pilot1Window = inDateRange(localYMDNumber, pilot1StartDate, pilot1EndDate);
        const pilot2Window = inDateRange(localYMDNumber, pilot2StartDate, pilot2EndDate);

        let pendingPilotGroup = null;
        if (pilot1Match && !pilot1Window) pendingPilotGroup = 'pilot1';
        if (pilot2Match && !pilot2Window) pendingPilotGroup = 'pilot2';

        //  REVERSED LOGIC: Pilot groups in active window  FORCE MFA (success)
        if (pilot1Match && pilot1Window) {
            debugInfo.evaluatedPath = 'pilot1';
            return {
                mfaDecision: {
                    success: true,
                    reason: `User in pilot1 active window (${pilot1StartDate}..${pilot1EndDate})  force MFA enrollment.`,
                    rolloutPercentage: rolloutPercentNum,
                    pilotGroup: 'pilot1',
                    pendingPilotGroup,
                    cutoffReached: false,
                    ...(debugMode ? { debugInfo } : {})
                }
            };
        }

        if (pilot2Match && pilot2Window) {
            debugInfo.evaluatedPath = 'pilot2';
            return {
                mfaDecision: {
                    success: true,
                    reason: `User in pilot2 active window (${pilot2StartDate}..${pilot2EndDate})  force MFA enrollment.`,
                    rolloutPercentage: rolloutPercentNum,
                    pilotGroup: 'pilot2',
                    pendingPilotGroup,
                    cutoffReached: false,
                    ...(debugMode ? { debugInfo } : {})
                }
            };
        }

        // -----------------------------
        // Random rollout
        // -----------------------------
        const roll = Math.random() * 100;
        const inRollout = roll < rolloutPercentNum;
        debugInfo.rollValue = roll.toFixed(2);
        debugInfo.evaluatedPath = 'random';

        if (!inRollout) {
            throw new Error(JSON.stringify({
                errorType: 'RANDOM_SKIP',
                reason: `User not selected for MFA (roll=${roll.toFixed(2)} >= ${rolloutPercentNum}).`,
                rolloutPercentage: rolloutPercentNum,
                pilotGroup: null,
                pendingPilotGroup,
                cutoffReached: false,
                ...(debugMode ? { debugInfo } : {})
            }));
        }

        // -----------------------------
        // Success  user randomly selected for MFA
        // -----------------------------
        return {
            mfaDecision: {
                success: true,
                reason: `User randomly selected for MFA (roll=${roll.toFixed(2)} < ${rolloutPercentNum}).`,
                rolloutPercentage: rolloutPercentNum,
                pilotGroup: null,
                pendingPilotGroup,
                cutoffReached: false,
                ...(debugMode ? { debugInfo } : {})
            }
        };

    } catch (err) {
        if (err instanceof Error && err.message.startsWith('{')) {
            throw err;
        }
        throw new Error(JSON.stringify({
            errorType: 'SYSTEM_ERROR',
            reason: err.message || String(err),
            rolloutPercentage: 0,
            pilotGroup: null,
            pendingPilotGroup: null,
            cutoffReached: false,
            debugInfo: { evaluatedPath: 'error', message: err.message || String(err) }
        }));
    }
};

