    //     moxieScript.src = "https://www.onelink-edge.com/moxie.min.js";
    //     moxieScript.setAttribute("data-oljs", moxieKey);
    //     targetDoc.head.appendChild(moxieScript);
    // }

    /**
     * Hides the modal container AND removes the background elements.
     */
    function closeModalAndBackground() {
        const modalContainer = document.getElementById('welcomeModal');
        if (modalContainer) {
            modalContainer.style.display = 'none';
        }
        removeBackgroundSiteDimmed();
    }

    // ========================================================================
    //                     Initialization Logic
    // ========================================================================
    console.log("Custom script initializing for the first time.");

    // 1. Load the background immediately.

    if (domain.includes("grandresidenceclub.com")) {

        insertBackgroundSiteDimmed("{{global.company.variables.backGroundImageGRCUrl}}");
        backgroundVisible = true;

    } else if (domain.includes("myritzcarltonclub.com")) {
        insertBackgroundSiteDimmed("{{global.company.variables.backGroundImageRCCUrl}}");
        backgroundVisible = true;
    }
    else {
        insertBackgroundSiteDimmed("{{global.company.variables.backGroundImageUrl}}");
        backgroundVisible = true;
    }

    // 2. Load the translation script, trusting it to handle its own logic.
    // injectMoxieScript();

    // 3. Set up a listener for the modal close buttons.
    window.top.document.body.addEventListener('click', function (event) {
        if (event.target.closest('#closeBtn') || event.target.closest('#modalX')) {
            closeModalAndBackground();
        }
    });

} // End of the main "if" block

