/* Base styling for the page */
body {
    margin: 0;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
    /* Prevents scrollbars from appearing when the iframe is present */
    overflow: hidden;
}

/*
 * ========================================
 *  Your Modal Styles
 * ========================================
 */

/* The main modal container */
.welcome-modal {
    display: flex;
    justify-content: center;
    align-items: center;
    position: fixed;
    top: 0;
    left: 0;
    height: 100vh;
    width: 100vw;
    /* IMPORTANT: The background property is removed from here */
    z-index: 9999;
    /* Ensures the modal is on top */
}

/* The white content box */
.modal-content {
    background: white;
    padding: 2rem;
    border-radius: 10px;
    min-width: 352px;
  max-width: 760px;
    width: 90%;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
    position: relative;
}

/* Title text */
.modal-title {
    color: var(--Black, black);
    font-size: 20px;
    font-family: Roboto;
    font-weight: 600;
    line-height: 30px;
    word-wrap: break-word;
    /* font-size: 1.5rem; */
    /* font-weight: bold; */
    margin-bottom: 1rem;

}

/* Subtitle text */
.modal-subtitle {
    /* font-size: 1rem; */
    /* line-height: 1.5; */
    /* margin-bottom: 1.5rem; */
    color: var(--Black, black);
    font-size: 16px;
    font-family: Roboto;
    font-weight: 400;
    line-height: 24px;
    word-wrap: break-word;

}

/* Primary "Close" button */
.custom-close-btn {

    height: 48px;
    gap: 12px;
    padding: 0px 24px;
    border-radius: 1000px;
    background: black;
    color: white;
    cursor: pointer;
    text-align: center;
    font-family: "Roboto";
    font-size: 16px;
    line-height: 24px;
    font-weight: 600;
    /* display: flex; */
    align-items: center;
    justify-content: center;
    /* background: none; */
    border: 1px solid black;
    margin-top: 12px;

}

/* Top-right "X" icon button */
.modal-crossBtn {
    position: absolute;
    top: 15px;
    right: 15px;
    background: transparent;
    border: none;
    padding: 0;
    cursor: pointer;
}

.modal-crossBtn img {
    width: 24px;
    height: 24px;
    display: block;
}



@media (max-width: 768px) {
    .modal-content {
        padding: 1.5rem;
        width: 80%;
    }

    .modal-title {
        font-size: 18px;
        line-height: 26px;
    }

    .modal-subtitle {
        font-size: 14px;
        line-height: 22px;
    }

    .custom-close-btn {
        font-size: 14px;
        padding: 10px 20px;
    }

    .modal-crossBtn img {
        width: 20px;
        height: 20px;
    }
}

@media (max-width: 480px) {
    .modal-content {
        padding: 1rem;
        border-radius: 8px;
    }

    .modal-title {
        font-size: 20px;
        line-height: 24px;
        width: 100%;
        max-width: 250px;
    }

    .modal-subtitle {
        font-size: 16px;
        line-height: 20px;
        max-width: 300px;
    }

