//document.addEventListener("DOMContentLoaded", function () {
const form = document.getElementById("connectOwner");
const formError = document.getElementById("formErrorMessage");
const errorText = document.getElementById("errorText");
let allowSubmit = false;
const fields = [
  { id: "firstName", errorId: "firstNameError", label: "First Name" },
  { id: "lastName", errorId: "lastNameError", label: "Last Name" },
  { id: "ownerId", errorId: "ownerIdError", label: "Customer ID" }
];

const touchedFields = {};


// Attach blur and input events
fields.forEach(({ id, errorId, label }) => {
  const input = document.getElementById(id);
  const error = document.getElementById(errorId);

  input.addEventListener("blur", () => {
    touchedFields[id] = true;
    checkInput(input, error, label);
  });

  input.addEventListener("input", () => {
    if (touchedFields[id]) {
      console.log("Field touched")
      checkInput(input, error, label);
    }
  });

  // if(input.id.trim()===""){
  //   formError.style.display="block";
  //   errorText.textContent="Please enter below data."
  // }
});

// Handle form submission
const submitBtn = document.getElementById('submit');
submitBtn.addEventListener("click", function (e) {
  e.preventDefault();
  let isValid = true;
  // formError.style.display = "none";
  // formError.textContent = "";

  fields.forEach(({ id, errorId, label }) => {
    const input = document.getElementById(id);
    const error = document.getElementById(errorId);
    touchedFields[id] = true;

    const fieldIsValid = checkInput(input, error, label);
    if (!fieldIsValid) {
      isValid = false;
      formError.style.display = "flex";
      formError.textContent = "Please fill out the required fields correctly."
    }
  });

  if (!isValid) {
    // formError.style.display = "flex";
    // formError.textContent = "Please fill out the required fields correctly.";
    console.log("form is invalid")
    e.preventDefault();// stopping form submit
    e.stopPropagation();
    allowSubmit = false;
    // Focus first invalid field
    const firstInvalid = fields.find(({ id }) => {
      return !validateInput(document.getElementById(id));
    });
    if (firstInvalid) {
      document.getElementById(firstInvalid.id).focus();
    }


  }
  else {
    console.log("Form is valid. Submitting...");
    formError.style.display = "none";
    allowSubmit = true;
  }
});

// Validates input and shows/hides error
function checkInput(input, errorElement, label) {
  // const id = input.id;
  const value = input.value.trim();
  //const expected = expectedValues[id];


  if (!validateInput(input)) {
    let message = `Enter your ${label}`;

    // if (input.type === "email" && value !== "") {
    //   message = `Please enter a valid email address`;
    // }

    showError(input, errorElement, message);
    return false;
  } else {
    hideError(input, errorElement);
    return true;
  }
}




// Returns true if input is valid
function validateInput(input) {
  const value = input.value.trim();

  if (value === "") {
    console.log("NO value")
    return false;
  }


  return true;
}

// Show error message
function showError(input, errorElement, message) {
  input.style.border = "1px solid red";
  errorElement.style.color = "red";
  errorElement.style.fontSize = "13px";
  errorElement.textContent = message;
}

// Hide error
function hideError(input, errorElement) {
  input.style.border = "";
  errorElement.textContent = "";
  formError.style.display = "none";
}
