}

/* Login Page Validation End */

/* Forgot Password Validation Start */

var forgotPasswordEmail = document.getElementById('forgot-password-email');
var forgotPasswordEmailError = document.getElementById('forgot-password-email-error');
var forgotPasswordNextBtn = document.getElementById('forgot-password-next-btn');

if (forgotPasswordNextBtn) {
  forgotPasswordNextBtn.disabled = true;
}

if (forgotPasswordEmail) {
  forgotPasswordEmail.addEventListener('input', function () {
    if (validateEmail(forgotPasswordEmail.value)) {
      if (forgotPasswordEmailError) {
        forgotPasswordEmailError.textContent = '';
      }
      forgotPasswordEmail.parentElement
        .querySelector('input[type="email"]')
        .classList.remove('login--custom-input-error');
      forgotPasswordEmail.parentElement
        .querySelector('label[for="forgot-password-email"]')
        .classList.remove('login--custom-Label-error');
      forgotPasswordNextBtn.disabled = false; // enable next button when email is valid
    } else {
      if (forgotPasswordEmailError) {
        forgotPasswordEmailError.textContent = 'Please enter a valid email address';
      }
      forgotPasswordEmail.parentElement.querySelector('input[type="email"]').classList.add('login--custom-input-error');
      forgotPasswordEmail.parentElement
        .querySelector('label[for="forgot-password-email"]')
        .classList.add('login--custom-Label-error');
      forgotPasswordNextBtn.disabled = true; // disable next button when email is invalid
    }
  });
}

/* Forgot Password Validation End */

/* Create Account Validation Start */

var createAccountEmail = document.getElementById('create-account-email-address');
var createAccountPhoneNumber = document.getElementById('create-account-phone-number');
// var createAccountTermsAndService = document.getElementById('create-account-terms-and-service');
var createAccountZip = document.getElementById('create-account-zip-code');
