function checkUsername() {
    const userVal = usernameInput.value.trim();
    // let isValid = true;

    if (userVal === "") {
        usernameInput.classList.add("invalid");
        usernameError.textContent = "Username is required.";
        usernameError.style.display = "block";
        usernameError.style.marginTop = "0px";
        isValid = false;
    }
    else {
        usernameInput.classList.remove("invalid");
        usernameError.style.display = "none";
        isValid = true;
    }
    return isValid;
}

function checkPassword() {
    const val = passwordInput.value.trim();
    //let isValid = true;

    if (val === "") {
        passwordInput.classList.add("invalid");
        passwordError.textContent = "Password is required.";
        passwordError.style.display = "block";
        isValid = false;
    } else {
        const passwordValid = Object.values(rules).every((rule) => rule.test(val));
        if (!passwordValid) {
            passwordInput.classList.add("invalid");
            passwordError.textContent = "Password does not meet requirements.";
            passwordError.style.display = "block";
            isValid = false;
        } else {
            passwordInput.classList.remove("invalid");
            passwordError.style.display = "none";
        }
    }

    return isValid;
}

function checkConfirmPassword() {
    const val = passwordInput.value.trim();
    const confirmVal = confirmPassword.value.trim();
    //let isValid = true;

    if (confirmVal === "") {
        confirmPassword.classList.add("invalid");
        confirmPasswordError.textContent = "Please re-enter your password.";
        confirmPasswordError.style.display = "block";
        isValid = false;
    } else if (val !== confirmVal) {
        confirmPassword.classList.add("invalid");
        confirmPasswordError.textContent = "Passwords do not match.";
        confirmPasswordError.style.display = "block";
        isValid = false;
    } else {
        confirmPassword.classList.remove("invalid");
        confirmPasswordError.style.display = "none";
    }

    return isValid;
}

// Real-time validation on input
usernameInput.addEventListener("input", checkUsername);
passwordInput.addEventListener("input", checkPassword);
confirmPassword.addEventListener("input", checkConfirmPassword);

// On blur  show required/mismatch errors

usernameInput.addEventListener("blur", checkUsername);
passwordInput.addEventListener("blur", checkPassword);
confirmPassword.addEventListener("blur", checkConfirmPassword);

// Final validation function for form submission
function checkInputs() {
    const isUsername = checkUsername();
    const isPasswordValid = checkPassword();
    const isConfirmValid = checkConfirmPassword();
    return isUsername && isPasswordValid && isConfirmValid;
}


