function validatePasswordOnly() {
    const val = passwordInput.value;

    // Remove bullets dynamically when typing
    if (val.length > 0) {
        rulesListNew.classList.add("no-bullets");
    } else {
        rulesListNew.classList.remove("no-bullets");
    }

    // Check rules for password input only
    for (let rule in rulesNew) {
        if (rule === "match") continue; // skip match here
        if (rulesNew[rule].test(val)) {
            elementsNew[rule].classList.add("valid");
            elementsNew[rule].classList.remove("invalid");
        } else {
            elementsNew[rule].classList.add("invalid");
            elementsNew[rule].classList.remove("valid");
        }
    }
}

function validateConfirmPassword() {
    const val = passwordInput.value;
    const confirmVal = confirmPassword.value;

    // Remove bullets dynamically when typing
