    // }

    // Additional check  must match password
    if (val !== "" && confirmVal === val) {
        elementsNew?.match?.classList?.add("valid");
        elementsNew?.match?.classList?.remove("invalid");
    } else {
        elementsNew?.match?.classList?.add("invalid");
        elementsNew?.match?.classList?.remove("valid");
    }
}

